// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        city: 'all',
        typeId: 'all'
    });

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º —Ñ–∏–ª—å—Ç—Ä—ã –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
    useEffect(() => {
        if (product.selectedCity) {
            setFilters(prev => ({
                ...prev,
                city: product.selectedCity
            }));
        }
    }, [product.selectedCity]);

    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // –ï—Å–ª–∏ –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å –ø—É—Å—Ç–æ–π, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –æ–±—ã—á–Ω—ã–µ —Ç–æ–≤–∞—Ä—ã
            if (product.selectedCity) {
                await product.fetchProductsByCity(product.selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), filters);
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞:', error);
        } finally {
            setIsSearching(false);
        }
    };

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleFilterChange = (filterType, value) => {
        const newFilters = {
            ...filters,
            [filterType]: value
        };
        setFilters(newFilters);

        // –ï—Å–ª–∏ –µ—Å—Ç—å –∞–∫—Ç–∏–≤–Ω—ã–π –ø–æ–∏—Å–∫, –æ–±–Ω–æ–≤–ª—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å –Ω–æ–≤—ã–º–∏ —Ñ–∏–ª—å—Ç—Ä–∞–º–∏
        if (product.hasActiveSearch && searchQuery.trim()) {
            product.searchProducts(searchQuery.trim(), newFilters);
        }
    };

    const clearSearch = async () => {
        setSearchQuery('');
        if (product.selectedCity) {
            await product.fetchProductsByCity(product.selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">–ù–∞–π–¥–∏—Ç–µ –Ω—É–∂–Ω—ã–π —Ç–æ–≤–∞—Ä</h2>
                <div className="search-subtitle">–ë—ã—Å—Ç—Ä—ã–π –ø–æ–∏—Å–∫ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é, –æ–ø–∏—Å–∞–Ω–∏—é –∏ —Ü–µ–Ω–µ</div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">üîç</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleInputChange}
                        onKeyPress={handleKeyPress}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞..."
                        className="search-input"
                        disabled={isSearching}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ‚úï
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching || !searchQuery.trim()}
                    >
                        {isSearching ? (
                            <div className="search-spinner"></div>
                        ) : (
                            '–ü–æ–∏—Å–∫'
                        )}
                    </button>
                </div>
            </form>

            {/* –§–∏–ª—å—Ç—Ä—ã –≤—Å–µ–≥–¥–∞ –≤–∏–¥–Ω—ã –¥–ª—è –ª—É—á—à–µ–≥–æ UX */}
            <div className="filters-section">
                <div className="filters-header">
                    <span className="filters-title">–£—Ç–æ—á–Ω–∏—Ç–µ –ø–æ–∏—Å–∫</span>
                </div>
                <div className="filters-grid">
                    <div className="filter-group">
                        <label className="filter-label">–ì–æ—Ä–æ–¥</label>
                        <select
                            value={filters.city}
                            onChange={(e) => handleFilterChange('city', e.target.value)}
                            className="filter-select"
                        >
                            <option value="all">–í—Å–µ –≥–æ—Ä–æ–¥–∞</option>
                            {product.cities && product.cities.map(city => (
                                <option key={city} value={city}>
                                    {city}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="filter-group">
                        <label className="filter-label">–ö–∞—Ç–µ–≥–æ—Ä–∏—è</label>
                        <select
                            value={filters.typeId}
                            onChange={(e) => handleFilterChange('typeId', e.target.value)}
                            className="filter-select"
                        >
                            <option value="all">–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</option>
                            <option value="1">–≠–ª–µ–∫—Ç—Ä–æ–Ω–∏–∫–∞</option>
                            <option value="2">–û–¥–µ–∂–¥–∞</option>
                            <option value="3">–î–ª—è –¥–æ–º–∞</option>
                            <option value="4">–ê–≤—Ç–æ—Ç–æ–≤–∞—Ä—ã</option>
                        </select>
                    </div>

                    <div className="filter-actions">
                        <button
                            onClick={handleSearch}
                            className="apply-filters-button"
                            disabled={isSearching}
                        >
                            –ü—Ä–∏–º–µ–Ω–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä—ã
                        </button>
                    </div>
                </div>
            </div>

            {product.hasActiveSearch && product.products.length > 0 && (
                <div className="search-results-info">
                    <div className="results-count">
                        –ù–∞–π–¥–µ–Ω–æ —Ç–æ–≤–∞—Ä–æ–≤: <strong>{product.totalCount}</strong>
                    </div>
                    <div className="search-query">
                        –ü–æ –∑–∞–ø—Ä–æ—Å—É: "<strong>{product.currentSearchQuery}</strong>"
                        {filters.city !== 'all' && ` –≤ –≥–æ—Ä–æ–¥–µ ${filters.city}`}
                    </div>
                    <button 
                        onClick={clearSearch}
                        className="clear-search-link"
                    >
                        –°–±—Ä–æ—Å–∏—Ç—å –ø–æ–∏—Å–∫
                    </button>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
