// pages/ProductPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Spinner, Alert } from 'react-bootstrap';
import { fetchOneProduct } from '../http/productAPI';
import { observer } from 'mobx-react-lite';
import '../css/pages/ProductPage.css';
const ProductPage = observer(() => {
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        const getProduct = async () => {
            try {
                setLoading(true);
                const productData = await fetchOneProduct(id);
                setProduct(productData);
            } catch (e) {
                setError('Ошибка при загрузке товара');
                console.error('Error fetching product:', e);
            } finally {
                setLoading(false);
            }
        };

        getProduct();
    }, [id]);

    if (loading) {
        return (
            <Container className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
                <Spinner animation="border" role="status">
                    <span className="visually-hidden">Загрузка...</span>
                </Spinner>
            </Container>
        );
    }

    if (error) {
        return (
            <Container>
                <Alert variant="danger" className="mt-3">
                    {error}
                </Alert>
            </Container>
        );
    }

    if (!product) {
        return (
            <Container>
                <Alert variant="warning" className="mt-3">
                    Товар не найден
                </Alert>
            </Container>
        );
    }

    return (
        <Container className="mt-4">
            <Button 
                variant="outline-secondary" 
                onClick={() => history.goBack()} 
                className="mb-3"
            >
                ← Назад
            </Button>
            
            <Row>
                <Col md={6}>
                    <Card>
                        <Card.Img 
                            variant="top" 
                            src={process.env.REACT_APP_API_URL + product.img}
                            style={{ height: '400px', objectFit: 'cover' }}
                        />
                    </Card>
                </Col>
                
                <Col md={6}>
                    <Card>
                        <Card.Body>
                            <Card.Title className="h2">{product.name}</Card.Title>
                            <Card.Text className="h4 text-primary mb-3">
                                {product.price} $
                            </Card.Text>
                            
                            <div className="mb-3">
                                <strong>Город:</strong> {product.city}
                            </div>
                            
                            {product.description && (
                                <div className="mb-3">
                                    <strong>Описание:</strong>
                                    <p>{product.description}</p>
                                </div>
                            )}

                            {/* Дополнительная информация о товаре */}
                            {product.info && product.info.length > 0 && (
                                <div className="mb-3">
                                    <strong>Характеристики:</strong>
                                    {product.info.map((infoItem, index) => (
                                        <div key={index} className="mt-2">
                                            <strong>{infoItem.title}:</strong> {infoItem.description}
                                        </div>
                                    ))}
                                </div>
                            )}

                            <Button variant="primary" size="lg" className="w-100">
                                Связаться с продавцом
                            </Button>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
});

export default ProductPage;
