                                }}
                            >
                                {renderCityButtons()}
                                
                                {/* –õ–µ–≥–µ–Ω–¥–∞ –∫–∞—Ä—Ç—ã */}
                                <div 
                                    style={{
                                        position: 'absolute',
                                        bottom: '10px',
                                        left: '10px',
                                        background: 'rgba(255,255,255,0.9)',
                                        padding: '8px 12px',
