// routes.js
import Admin from "./pages/Admin";
import Auth from "./pages/Auth";
import ProductPage from "./pages/ProductPage"; // Импортируем ProductPage
import Shop from "./pages/Shop";
import { ADMIN_ROUTE, SHOP_ROUTE, PROFILE_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, PRODUCT_ROUTE } from "./utils/consts";
import UserProfile from './pages/UserInfo';

export const authRoutes = [
    {
        path: ADMIN_ROUTE,
        Component: Admin
    },
    { 
        path: PROFILE_ROUTE, 
        Component: UserProfile 
    }
];

export const publicRoutes = [
    {
        path: SHOP_ROUTE,
        Component: Shop
    },
    {
        path: LOGIN_ROUTE,
        Component: Auth
    },
    {
        path: REGISTRATION_ROUTE,
        Component: Auth
    },
    {
        path: PRODUCT_ROUTE + '/:id', // Добавляем маршрут для страницы продукта
        Component: ProductPage
    }
];
