import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import bigStar from '../assets/star.png'
import {useParams} from 'react-router-dom'
//import {fetchOneDevice} from "../http/deviceAPI";

const DevicePage = () => {

    const device =  {id: 1, name: 'ip 1 pro', price: 25000, rating: 5, img: 'https://yandex.by/images/search?img_url=https%3A%2F%2Fsun9-78.userapi.com%2Fimpg%2FoIN-UyIx68EvfFBVIi5TnbuvPsAvEvteQUw-nw%2F1k1mWhdKsOk.jpg%3Fsize%3D1080x1489%26quality%3D96%26sign%3D40082d544487df65170b8a3109c723a8%26c_uniq_tag%3Du0CxdjVqTdL5MO6IsLUW3ZGQ6cpeMd5MmCVYZK_8Goc%26type%3Dalbum&lr=153&pos=1&rpt=simage&source=serp&text=%D0%BA%D0%B0%D1%80%D1%82%D0%B8%D0%BD%D0%BA%D0%B0%20%D1%81%20%D0%B4%D0%BD%D0%B5%D0%BC%20%D1%80%D0%BE%D0%B6%D0%B4%D0%B5%D0%BD%D0%B8%D1%8F'}
       
    // const [device, setDevice] = useState({info: []})
    // const {id} = useParams()
    // useEffect(() => {
    //     fetchOneDevice(id).then(data => setDevice(data))
    // }, [])

    return (
        <Container className="mt-3">
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={process.env.REACT_APP_API_URL + device.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${bigStar}) no-repeat center center`, width:240, height: 240, backgroundSize: 'cover', fontSize:64}}
                        >
                            {device.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 300, fontSize: 32, border: '5px solid lightgray'}}
                    >
                        <h3>От: {device.price} руб.</h3>
                        <Button variant={"outline-dark"}>Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {/* {device.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )} */}
            </Row>
        </Container>
    );
};

export default DevicePage;
