import React, { useContext } from "react";
import { Context } from "../index";
import { observer } from "mobx-react-lite";
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Button from "react-bootstrap/esm/Button";

const NavBar = () => {
    const { user } = useContext(Context);

    return (
        <Navbar collapseOnSelect expand="lg" style={{ backgroundColor: '#ffff00ff' }}>
            <Container>
                <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="me-auto">
                       
                    </Nav>
                    {user.isAuth ?
                        <Nav>
                            <Button variant={"outline-dark"}>Админ панель</Button>
                            <Button variant={"outline-dark"}>Войти</Button>
                        </Nav>
                        :
                        <Nav>
                            <Button variant={"outline-dark"} onClick={()=>user.setIsAuth(true)}>Авторизоваться</Button>
                         
                        </Nav>
                    }

                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}

export default NavBar;
