// components/modals/CitySelector.js
import React, { useState, useEffect } from 'react';
import { Modal, Button, Tabs, Tab, Spinner, Form } from 'react-bootstrap';

const CitySelectorModal = ({ 
    show, 
    handleClose, 
    cities, 
    selectedCity, 
    handleCityChange,
    onDetectLocation,
    isLoading 
}) => {
    const [activeTab, setActiveTab] = useState('list');
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredCities, setFilteredCities] = useState([]);
    const [map, setMap] = useState(null);
    const [mapLoaded, setMapLoaded] = useState(false);

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –≥–æ—Ä–æ–¥–æ–≤ –ø—Ä–∏ –ø–æ–∏—Å–∫–µ
    useEffect(() => {
        if (searchTerm) {
            const filtered = cities.filter(city => 
                city.toLowerCase().includes(searchTerm.toLowerCase())
            );
            setFilteredCities(filtered);
        } else {
            setFilteredCities(cities);
        }
    }, [searchTerm, cities]);

    // –ó–∞–≥—Ä—É–∑–∫–∞ –Ø–Ω–¥–µ–∫—Å.–ö–∞—Ä—Ç
    useEffect(() => {
        if (show && activeTab === 'map' && !mapLoaded) {
            const script = document.createElement('script');
            script.src = 'https://api-maps.yandex.ru/2.1/?lang=ru_RU';
            script.async = true;
            script.onload = () => {
                window.ymaps.ready(() => {
                    initMap();
                    setMapLoaded(true);
                });
            };
            document.head.appendChild(script);

            return () => {
                // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
                if (map) {
                    map.destroy();
                }
            };
        }
    }, [show, activeTab, mapLoaded]);

    const initMap = () => {
        const yandexMap = new window.ymaps.Map('yandex-map', {
            center: [53.9, 27.5], // –¶–µ–Ω—Ç—Ä –ë–µ–ª–∞—Ä—É—Å–∏
            zoom: 6,
            controls: ['zoomControl', 'fullscreenControl']
        });

        // –î–æ–±–∞–≤–ª—è–µ–º –ø–æ–∏—Å–∫
        const searchControl = new window.ymaps.control.SearchControl({
            options: {
                provider: 'yandex#search',
                noPlacemark: true
            }
        });
        yandexMap.controls.add(searchControl);

        // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤—ã–±–æ—Ä–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –ø–æ–∏—Å–∫–∞
        searchControl.events.add('resultselect', function (e) {
            const results = searchControl.getResultsArray();
            const selectedResult = results[e.get('index')];
            const cityName = extractCityName(selectedResult);
            
            if (cityName && cities.includes(cityName)) {
                handleCitySelection(cityName);
            }
        });

        // –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–∫–∏ –¥–ª—è –≥–æ—Ä–æ–¥–æ–≤
        cities.forEach(city => {
            // –ü–æ–ª—É—á–∞–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≥–æ—Ä–æ–¥–∞ (—É–ø—Ä–æ—â–µ–Ω–Ω–æ)
            const coords = getCityCoordinates(city);
            if (coords) {
                const placemark = new window.ymaps.Placemark(coords, {
                    balloonContent: `<strong>${city}</strong>`,
                    hintContent: city
                }, {
                    preset: selectedCity === city ? 'islands#blueIcon' : 'islands#redIcon'
                });

                placemark.events.add('click', () => {
                    handleCitySelection(city);
                });

                yandexMap.geoObjects.add(placemark);
            }
        });

        setMap(yandexMap);
    };

    const getCityCoordinates = (city) => {
        // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –æ—Å–Ω–æ–≤–Ω—ã—Ö –≥–æ—Ä–æ–¥–æ–≤ –ë–µ–ª–∞—Ä—É—Å–∏
        const coordinates = {
            '–ú–∏–Ω—Å–∫': [53.9, 27.5],
            '–ë—Ä–µ—Å—Ç': [52.1, 23.7],
            '–ì—Ä–æ–¥–Ω–æ': [53.7, 23.8],
            '–ì–æ–º–µ–ª—å': [52.4, 31.0],
            '–í–∏—Ç–µ–±—Å–∫': [55.2, 30.2],
            '–ú–æ–≥–∏–ª—ë–≤': [53.9, 30.3],
            '–ë–∞—Ä–∞–Ω–æ–≤–∏—á–∏': [53.1, 26.0],
            '–ë–æ—Ä–∏—Å–æ–≤': [54.2, 28.5],
            '–ü–∏–Ω—Å–∫': [52.1, 26.1],
            '–û—Ä—à–∞': [54.5, 30.4],
            '–õ–∏–¥–∞': [53.9, 25.3],
            '–ù–æ–≤–æ–ø–æ–ª–æ—Ü–∫': [55.5, 28.6],
            '–ú–æ–ª–æ–¥–µ—á–Ω–æ': [54.3, 26.8],
            '–ü–æ–ª–æ—Ü–∫': [55.5, 28.8],
            '–ñ–ª–æ–±–∏–Ω': [52.9, 30.0]
        };
        return coordinates[city];
    };

    const extractCityName = (result) => {
        const name = result.properties.get('name');
        // –ü—ã—Ç–∞–µ–º—Å—è –Ω–∞–π—Ç–∏ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ —Å –Ω–∞—à–∏–º —Å–ø–∏—Å–∫–æ–º –≥–æ—Ä–æ–¥–æ–≤
        return cities.find(city => name.includes(city));
    };

    const handleCitySelection = (cityName) => {
        const mockEvent = {
            target: { value: cityName }
        };
        handleCityChange(mockEvent);
        handleClose();
    };

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –≤–∫–ª–∞–¥–æ–∫
    const handleTabSelect = (tab) => {
        setActiveTab(tab);
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleCitySelectFromList = (city) => {
        const mockEvent = {
            target: { value: city }
        };
        handleCityChange(mockEvent);
        handleClose();
    };

    // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –¥–ª—è —Å—Ç–∞—Ç–∏—á–Ω–æ–π –∫–∞—Ä—Ç—ã (–≤ –ø—Ä–æ—Ü–µ–Ω—Ç–∞—Ö –æ—Ç —Ä–∞–∑–º–µ—Ä–æ–≤ –∫–∞—Ä—Ç—ã)
    const cityCoordinates = {
        '–ú–∏–Ω—Å–∫': { top: '45%', left: '42%' },
        '–ë—Ä–µ—Å—Ç': { top: '72%', left: '22%' },
        '–ì—Ä–æ–¥–Ω–æ': { top: '38%', left: '28%' },
        '–ì–æ–º–µ–ª—å': { top: '58%', left: '72%' },
        '–í–∏—Ç–µ–±—Å–∫': { top: '28%', left: '62%' },
        '–ú–æ–≥–∏–ª—ë–≤': { top: '48%', left: '68%' },
        '–ë–∞—Ä–∞–Ω–æ–≤–∏—á–∏': { top: '55%', left: '35%' },
        '–ë–æ—Ä–∏—Å–æ–≤': { top: '42%', left: '52%' },
        '–ü–∏–Ω—Å–∫': { top: '65%', left: '30%' },
        '–û—Ä—à–∞': { top: '38%', left: '58%' }
    };

    // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∫–Ω–æ–ø–æ–∫ –≥–æ—Ä–æ–¥–æ–≤ –Ω–∞ —Å—Ç–∞—Ç–∏—á–Ω–æ–π –∫–∞—Ä—Ç–µ
    const renderCityButtons = () => {
        return Object.entries(cityCoordinates).map(([cityName, coords]) => {
            // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–ª—å–∫–æ —Ç–µ –≥–æ—Ä–æ–¥–∞, –∫–æ—Ç–æ—Ä—ã–µ –µ—Å—Ç—å –≤ —Å–ø–∏—Å–∫–µ
            if (!cities.includes(cityName)) return null;

            return (
                <button
                    key={cityName}
                    style={{
                        position: 'absolute',
                        top: coords.top,
                        left: coords.left,
                        background: selectedCity === cityName ? '#007bff' : 'rgba(255,255,255,0.95)',
                        border: `2px solid ${selectedCity === cityName ? '#0056b3' : '#007bff'}`,
                        color: selectedCity === cityName ? 'white' : '#007bff',
                        fontWeight: 'bold',
                        cursor: 'pointer',
                        fontSize: '11px',
                        padding: '6px 10px',
                        borderRadius: '15px',
                        boxShadow: '0 2px 8px rgba(0,0,0,0.3)',
                        transform: 'translate(-50%, -50%)',
                        zIndex: 2,
                        minWidth: '70px',
                        transition: 'all 0.2s ease',
                        whiteSpace: 'nowrap'
                    }}
                    onClick={() => handleCitySelection(cityName)}
                    title={cityName}
                    onMouseEnter={(e) => {
                        if (selectedCity !== cityName) {
                            e.target.style.background = '#e3f2fd';
                            e.target.style.transform = 'translate(-50%, -50%) scale(1.1)';
                        }
                    }}
                    onMouseLeave={(e) => {
                        if (selectedCity !== cityName) {
                            e.target.style.background = 'rgba(255,255,255,0.95)';
                            e.target.style.transform = 'translate(-50%, -50%) scale(1)';
                        }
                    }}
                >
                    {cityName}
                </button>
            );
        });
    };

    return (
        <Modal show={show} onHide={handleClose} size="lg">
            <Modal.Header closeButton>
                <Modal.Title>–í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Tabs activeKey={activeTab} onSelect={handleTabSelect} className="mb-3">
                    <Tab eventKey="list" title="üìã –°–ø–∏—Å–æ–∫ –≥–æ—Ä–æ–¥–æ–≤">
                        <div style={{ marginBottom: '15px' }}>
                            <Button 
                                variant="outline-primary" 
                                onClick={onDetectLocation}
                                disabled={isLoading}
                                className="mb-3"
                                size="sm"
                            >
                                {isLoading ? (
                                    <>
                                        <Spinner animation="border" size="sm" className="me-2" />
                                        –û–ø—Ä–µ–¥–µ–ª—è–µ–º...
                                    </>
                                ) : (
                                    'üéØ –û–ø—Ä–µ–¥–µ–ª–∏—Ç—å –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏'
                                )}
                            </Button>

                            <Form.Group>
                                <Form.Label>
                                    <strong>–ü–æ–∏—Å–∫ –≥–æ—Ä–æ–¥–∞:</strong>
                                </Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="–ù–∞—á–Ω–∏—Ç–µ –≤–≤–æ–¥–∏—Ç—å –Ω–∞–∑–≤–∞–Ω–∏–µ –≥–æ—Ä–æ–¥–∞..."
                                    value={searchTerm}
                                    onChange={handleSearchChange}
                                    style={{ borderRadius: '8px' }}
                                />
                            </Form.Group>
                        </div>

                        <div style={{ maxHeight: '350px', overflowY: 'auto' }}>
                            <Form.Label>
                                <strong>–î–æ—Å—Ç—É–ø–Ω—ã–µ –≥–æ—Ä–æ–¥–∞ ({filteredCities.length}):</strong>
                            </Form.Label>
                            {filteredCities.length > 0 ? (
                                <div className="list-group">
                                    {filteredCities.map((city) => (
                                        <button
                                            key={city}
                                            type="button"
                                            className={`list-group-item list-group-item-action ${
                                                selectedCity === city ? 'active' : ''
                                            }`}
                                            onClick={() => handleCitySelectFromList(city)}
                                            style={{
                                                border: 'none',
                                                borderBottom: '1px solid #eee',
                                                borderRadius: '0'
                                            }}
                                        >
                                            <div className="d-flex justify-content-between align-items-center">
                                                <span>{city}</span>
                                                {selectedCity === city && (
                                                    <span className="badge bg-primary">‚úì –í—ã–±—Ä–∞–Ω</span>
                                                )}
                                            </div>
                                        </button>
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center text-muted py-4">
                                    {searchTerm ? (
                                        <div>
                                            <div style={{ fontSize: '48px', marginBottom: '10px' }}>üîç</div>
                                            <div>–ì–æ—Ä–æ–¥ "{searchTerm}" –Ω–µ –Ω–∞–π–¥–µ–Ω</div>
                                            <small>–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å –∑–∞–ø—Ä–æ—Å</small>
                                        </div>
                                    ) : (
                                        <div>
                                            <Spinner animation="border" className="me-2" />
                                            –ó–∞–≥—Ä—É–∑–∫–∞ –≥–æ—Ä–æ–¥–æ–≤...
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    </Tab>

                    <Tab eventKey="static-map" title="üó∫Ô∏è –ù–∞ –∫–∞—Ä—Ç–µ">
                        <div style={{ marginBottom: '15px' }}>
                            <p className="text-muted mb-3">
                                –í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥ –∫–ª–∏–∫–æ–º –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é –Ω–∞ –∫–∞—Ä—Ç–µ:
                            </p>
                            
                            {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å—Ç–∞—Ç–∏—á–Ω–æ–π –∫–∞—Ä—Ç—ã */}
                            <div 
                                style={{ 
                                    position: 'relative', 
                                    width: '100%', 
                                    height: '400px',
                                    backgroundImage: 'url(https://upload.wikimedia.org/wikipedia/commons/thumb/d/d3/Belarus_%28centered%29.svg/800px-Belarus_%28centered%29.svg.png)',
                                    backgroundSize: 'contain',
                                    backgroundRepeat: 'no-repeat',
                                    backgroundPosition: 'center',
                                    backgroundColor: '#e8f4f8',
                                    border: '2px solid #dee2e6',
                                    borderRadius: '8px',
                                    boxShadow: 'inset 0 0 10px rgba(0,0,0,0.1)'
                                }}
                            >
                                {renderCityButtons()}
                                
                                {/* –õ–µ–≥–µ–Ω–¥–∞ –∫–∞—Ä—Ç—ã */}
                                <div 
                                    style={{
                                        position: 'absolute',
                                        bottom: '10px',
                                        left: '10px',
                                        background: 'rgba(255,255,255,0.9)',
                                        padding: '8px 12px',
                                        borderRadius: '6px',
                                        fontSize: '12px',
                                        border: '1px solid #ddd'
                                    }}
                                >
                                    <strong>–ö–∞—Ä—Ç–∞ –ë–µ–ª–∞—Ä—É—Å–∏</strong>
                                </div>
                            </div>
                            
                            <div className="mt-3">
                                <small className="text-muted">
                                    üí° <strong>–ü–æ–¥—Å–∫–∞–∑–∫–∞:</strong> –ù–∞–≤–µ–¥–∏—Ç–µ –∫—É—Ä—Å–æ—Ä –Ω–∞ –≥–æ—Ä–æ–¥ –¥–ª—è —É–≤–µ–ª–∏—á–µ–Ω–∏—è
                                </small>
                            </div>
                        </div>
                    </Tab>

                    <Tab eventKey="interactive-map" title="üåç –ò–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω–∞—è –∫–∞—Ä—Ç–∞">
                        <div style={{ height: '400px', position: 'relative' }}>
                            <div 
                                id="yandex-map" 
                                style={{ 
                                    width: '100%', 
                                    height: '100%',
                                    borderRadius: '8px'
                                }}
                            >
                                {!mapLoaded && (
                                    <div style={{
                                        position: 'absolute',
                                        top: '50%',
                                        left: '50%',
                                        transform: 'translate(-50%, -50%)',
                                        textAlign: 'center'
                                    }}>
                                        <Spinner animation="border" />
                                        <div className="mt-2">–ó–∞–≥—Ä—É–∑–∫–∞ –∫–∞—Ä—Ç—ã...</div>
                                    </div>
                                )}
                            </div>
                        </div>
                        <div className="mt-3">
                            <small className="text-muted">
