// pages/UserInfo.js
import React, { useEffect, useState, useContext } from 'react';
import { getUserInfo, getSellerInfo, updateUser, updateSeller } from '../http/userAPI';
import { Context } from '../index';
import { observer } from "mobx-react-lite";
import '../UserInfo.css';

const UserInfo = () => {
    const { user } = useContext(Context);
    const [seller, setSeller] = useState(null);
    const [sellerProducts, setSellerProducts] = useState([]);
    const [editingUser, setEditingUser] = useState(false);
    const [editingSeller, setEditingSeller] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [updatedUser, setUpdatedUser] = useState({ 
        username: user.user.username, 
        email: user.user.email, 
        phone: user.user.phone 
    });
    const [updatedSeller, setUpdatedSeller] = useState({ 
        name: '', 
        description: '', 
        img: null
    });
    const [updatedProduct, setUpdatedProduct] = useState({
        name: '',
        price: '',
        description: '',
        city: '',
        price_type: 'fixed',
        price_text: '',
        currency: 'BYN'
    });
    const [imagePreview, setImagePreview] = useState('');
    const userId = user.user.id;

    useEffect(() => {
        const fetchData = async () => {
            if (!userId) return;

            try {
                const userInfo = await getUserInfo(userId);
                user.setUser(userInfo);
                if (user.user.id) {
                    const sellerData = await getSellerInfo(user.user.id);
                    setSeller(sellerData);
                    setUpdatedSeller({ 
                        name: sellerData.name, 
                        description: sellerData.description, 
                        img: null 
                    });
                    setImagePreview(sellerData.img);
                    
                    // Загружаем товары продавца
                    if (sellerData.id) {
                        await loadSellerProducts(sellerData.id);
                    }
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
            }
        };

        fetchData();
    }, [userId]);

    const loadSellerProducts = async (sellerId) => {
        try {
            const response = await fetch(`http://localhost:5000/api/seller/${sellerId}/products`);
            if (response.ok) {
                const products = await response.json();
                setSellerProducts(products);
            }
        } catch (error) {
            console.error('Ошибка при загрузке товаров:', error);
        }
    };

    const handleUserUpdate = async (e) => {
        e.preventDefault();
        try {
            await updateUser(userId, updatedUser);
            user.setUser({ ...user.user, ...updatedUser });
            setEditingUser(false);
        } catch (error) {
            console.error('Ошибка при обновлении пользователя:', error);
        }
    };

    const handleSellerUpdate = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('name', updatedSeller.name);
            formData.append('description', updatedSeller.description);
            
            if (updatedSeller.img) {
                formData.append('img', updatedSeller.img);
            }

            const updatedSellerData = await updateSeller(seller.id, formData);
            
            setSeller(updatedSellerData);
            setEditingSeller(false);
            setUpdatedSeller({ ...updatedSeller, img: null });
            
        } catch (error) {
            console.error('Ошибка при обновлении продавца:', error);
        }
    };

    const handleProductUpdate = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('name', updatedProduct.name);
            formData.append('price', updatedProduct.price);
            formData.append('description', updatedProduct.description);
            formData.append('city', updatedProduct.city);
            formData.append('price_type', updatedProduct.price_type);
            formData.append('price_text', updatedProduct.price_text);
            formData.append('currency', updatedProduct.currency);
            formData.append('sellerId', userId);

            const response = await fetch(`http://localhost:5000/api/prod/${editingProduct.id}`, {
                method: 'PUT',
                body: formData,
            });

            if (response.ok) {
                await loadSellerProducts(seller.id);
                setEditingProduct(null);
                setUpdatedProduct({
                    name: '',
                    price: '',
                    description: '',
                    city: '',
                    price_type: 'fixed',
                    price_text: '',
                    currency: 'BYN'
                });
            } else {
                throw new Error('Ошибка при обновлении товара');
            }
        } catch (error) {
            console.error('Ошибка при обновлении товара:', error);
            alert('Не удалось обновить товар');
        }
    };

    const handleDeleteProduct = async (productId) => {
        if (window.confirm('Вы уверены, что хотите удалить этот товар?')) {
            try {
                const response = await fetch(`http://localhost:5000/api/prod/${productId}`, {
                    method: 'DELETE',
                });

                if (response.ok) {
                    await loadSellerProducts(seller.id);
                } else {
                    throw new Error('Ошибка при удалении товара');
                }
            } catch (error) {
                console.error('Ошибка при удалении товара:', error);
                alert('Не удалось удалить товар');
            }
        }
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setUpdatedSeller({ ...updatedSeller, img: file });
            
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const cancelEditSeller = () => {
        setEditingSeller(false);
        setUpdatedSeller({ 
            name: seller.name, 
            description: seller.description, 
            img: null 
        });
        setImagePreview(seller.img);
    };

    const startEditProduct = (product) => {
        setEditingProduct(product);
        setUpdatedProduct({
            name: product.name,
            price: product.price || '',
            description: product.description || '',
            city: product.city,
            price_type: product.price_type || 'fixed',
            price_text: product.price_text || '',
            currency: product.currency || 'BYN'
        });
    };

    const cancelEditProduct = () => {
        setEditingProduct(null);
        setUpdatedProduct({
            name: '',
            price: '',
            description: '',
            city: '',
            price_type: 'fixed',
            price_text: '',
            currency: 'BYN'
        });
    };

    return (
        <div className="user-info-container">
            <h1>Личный кабинет</h1>
            <div className="user-info">
                <div className="user-details">
                    {!editingUser ? (
                        <div className="info-display">
                            <p><strong>Имя:</strong> {user.user.username}</p>
                            <p><strong>Email:</strong> {user.user.email}</p>
                            <p><strong>Телефон:</strong> {user.user.phone}</p>
                            <button className="edit-button" onClick={() => setEditingUser(true)}>
                                Редактировать
                            </button>
                        </div>
                    ) : (
                        <form className="info-edit" onSubmit={handleUserUpdate}>
                            <input
                                type="text"
                                value={updatedUser.username}
                                onChange={(e) => setUpdatedUser({ ...updatedUser, username: e.target.value })}
                                placeholder="Имя"
                            />
                            <input
                                type="email"
                                value={updatedUser.email}
                                onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })}
                                placeholder="Email"
                            />
                            <input
                                type="tel"
                                value={updatedUser.phone}
                                onChange={(e) => setUpdatedUser({ ...updatedUser, phone: e.target.value })}
                                placeholder="Телефон"
                            />
                            <button type="submit" className="save-button">Сохранить</button>
                            <button type="button" onClick={() => setEditingUser(false)} className="cancel-button">
                                Отмена
                            </button>
                        </form>
                    )}
                </div>
                
                {seller && (
                    <div className="seller-info">
                        <h2>Профиль продавца</h2>
                        <div className="seller-profile">
                            <div className="seller-image-section">
                                <div className="image-wrapper">
                                    <img 
                                        src={imagePreview || seller.img} 
                                        alt={seller.name} 
                                        className="seller-avatar" 
                                    />
                                </div>
                                {!editingSeller && (
                                    <button className="edit-profile-button" onClick={() => setEditingSeller(true)}>
                                        Редактировать профиль
                                    </button>
                                )}
                            </div>
                            
                            {!editingSeller ? (
                                <div className="seller-details">
                                    <p><strong>Название магазина:</strong> {seller.name}</p>
                                    <p><strong>Описание:</strong> {seller.description}</p>
                                    <p><strong>Рейтинг:</strong> {seller.rating ? seller.rating.toFixed(1) : 'Нет оценок'}</p>
                                </div>
                            ) : (
                                <form className="seller-edit-form" onSubmit={handleSellerUpdate}>
                                    <div className="form-group">
                                        <label>Название магазина:</label>
                                        <input
                                            type="text"
                                            value={updatedSeller.name}
                                            onChange={(e) => setUpdatedSeller({ ...updatedSeller, name: e.target.value })}
                                            placeholder="Введите название магазина"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Описание:</label>
                                        <textarea
                                            value={updatedSeller.description}
                                            onChange={(e) => setUpdatedSeller({ ...updatedSeller, description: e.target.value })}
                                            placeholder="Расскажите о вашем магазине"
                                            rows="4"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Изображение профиля:</label>
                                        <input
                                            type="file"
                                            accept="image/*"
                                            onChange={handleImageChange}
                                            className="file-input"
                                        />
                                    </div>
                                    <div className="form-actions">
                                        <button type="submit" className="save-button">Сохранить изменения</button>
                                        <button type="button" onClick={cancelEditSeller} className="cancel-button">
                                            Отмена
                                        </button>
                                    </div>
                                </form>
                            )}
                        </div>

                        {/* Секция товаров продавца */}
                        <div className="seller-products-section">
                            <h3>Мои товары ({sellerProducts.length})</h3>
                            
                            {sellerProducts.length === 0 ? (
                                <div className="no-products">
                                    У вас пока нет товаров
                                </div>
                            ) : (
                                <div className="products-grid">
                                    {sellerProducts.map(product => (
                                        <div key={product.id} className="product-card">
                                            <div className="product-image">
                                                <img 
                                                    src={product.img} 
                                                    alt={product.name}
                                                    onError={(e) => {
                                                        e.target.style.display = 'none';
                                                        e.target.nextSibling.style.display = 'flex';
                                                    }}
                                                />
                                                <div className="product-image-placeholder">
                                                    Товар
                                                </div>
                                            </div>
                                            <div className="product-info">
                                                <h4 className="product-name">{product.name}</h4>
                                                <p className="product-city">{product.city}</p>
                                                <div className="product-price">
                                                    {product.price_type === 'fixed' && product.price ? (
                                                        `${product.price} ${product.currency || 'BYN'}`
                                                    ) : product.price_type === 'negotiable' ? (
                                                        'Договорная'
                                                    ) : (
                                                        product.price_text || 'Цена не указана'
                                                    )}
                                                </div>
                                                <div className="product-actions">
                                                    <button 
                                                        className="edit-product-btn"
                                                        onClick={() => startEditProduct(product)}
                                                    >
                                                        Редактировать
                                                    </button>
                                                    <button 
                                                        className="delete-product-btn"
                                                        onClick={() => handleDeleteProduct(product.id)}
                                                    >
                                                        Удалить
                                                    </button>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}

                            {/* Форма редактирования товара */}
                            {editingProduct && (
                                <div className="edit-product-form">
                                    <h4>Редактирование товара</h4>
                                    <form onSubmit={handleProductUpdate}>
                                        <div className="form-row">
                                            <div className="form-group">
                                                <label>Название товара:</label>
                                                <input
                                                    type="text"
                                                    value={updatedProduct.name}
                                                    onChange={(e) => setUpdatedProduct({ ...updatedProduct, name: e.target.value })}
                                                    required
                                                />
                                            </div>
                                            <div className="form-group">
                                                <label>Цена:</label>
                                                <input
                                                    type="number"
                                                    value={updatedProduct.price}
                                                    onChange={(e) => setUpdatedProduct({ ...updatedProduct, price: e.target.value })}
                                                    step="0.01"
                                                />
                                            </div>
                                        </div>
                                        <div className="form-group">
                                            <label>Описание:</label>
                                            <textarea
                                                value={updatedProduct.description}
                                                onChange={(e) => setUpdatedProduct({ ...updatedProduct, description: e.target.value })}
                                                rows="3"
                                            />
                                        </div>
                                        <div className="form-row">
                                            <div className="form-group">
                                                <label>Город:</label>
                                                <input
                                                    type="text"
                                                    value={updatedProduct.city}
                                                    onChange={(e) => setUpdatedProduct({ ...updatedProduct, city: e.target.value })}
                                                    required
                                                />
                                            </div>
                                            <div className="form-group">
                                                <label>Тип цены:</label>
                                                <select
                                                    value={updatedProduct.price_type}
                                                    onChange={(e) => setUpdatedProduct({ ...updatedProduct, price_type: e.target.value })}
                                                >
                                                    <option value="fixed">Фиксированная</option>
                                                    <option value="negotiable">Договорная</option>
                                                    <option value="custom">Особая</option>
                                                </select>
                                            </div>
                                        </div>
                                        {updatedProduct.price_type === 'custom' && (
                                            <div className="form-group">
                                                <label>Текст цены:</label>
                                                <input
                                                    type="text"
                                                    value={updatedProduct.price_text}
                                                    onChange={(e) => setUpdatedProduct({ ...updatedProduct, price_text: e.target.value })}
                                                    placeholder="Например: Бесплатно, Обмен и т.д."
                                                />
                                            </div>
                                        )}
                                        <div className="form-actions">
                                            <button type="submit" className="save-button">Сохранить изменения</button>
                                            <button type="button" onClick={cancelEditProduct} className="cancel-button">
                                                Отмена
                                            </button>
                                        </div>
                                    </form>
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

