import React, { useEffect, useState, useContext } from 'react';
import { getUserInfo, getSellerInfo } from '../http/userAPI';
import { Context } from '../index';
import { observer } from "mobx-react-lite";

const UserInfo = () => {
    const { user } = useContext(Context);
    const [seller, setSeller] = useState(null);
    const userId = user.user.id;

    useEffect(() => {
        const fetchData = async () => {
            if (!userId) return;

            try {
               const userInfo = await getUserInfo(userId);
console.log('Данные перед установкой:', userInfo);
user.setUser(userInfo);
console.log('Текущие данные пользователя:', user.user);
                if (userInfo.seller_id) {
                    const sellerData = await getSellerInfo(userInfo.seller_id);
                    setSeller(sellerData);
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
            }
        };

        fetchData();
    }, [userId]);

    return (
        <div>
            <h1>Информация о пользователе</h1>
            <p>Имя: {user.user.username}</p>
            <p>Email: {user.user.email}</p>
            <p>Телефон: {user.user.phone}</p>

            {seller && (
                <div>
                    <h2>Информация о продавце</h2>
                    <p>Имя: {seller.name}</p>
                    <p>Описание: {seller.description}</p>
                    <img src={seller.img} alt={seller.name} />
                </div>
            )}
        </div>
    );
};

export default observer(UserInfo);
