// components/SearchBar.js
import React, { useState, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        minPrice: '',
        maxPrice: '',
        sortBy: 'newest', // newest, price_asc, price_desc
    });

    // –ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // –ï—Å–ª–∏ –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å –ø—É—Å—Ç–æ–π, —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –ø–æ–∏—Å–∫
            await product.clearSearch();
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), filters);
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // –û—á–∏—Å—Ç–∫–∞ –ø–æ–∏—Å–∫–∞
    const clearSearch = async () => {
        setSearchQuery('');
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest'
        });
        await product.clearSearch();
    };

    // –û—á–∏—Å—Ç–∫–∞ —Ñ–∏–ª—å—Ç—Ä–æ–≤
    const clearFilters = async () => {
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest'
        });
        await product.fetchProducts();
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤
    const handleFilterChange = (filterType, value) => {
        const newFilters = {
            ...filters,
            [filterType]: value
        };
        setFilters(newFilters);

        // –ï—Å–ª–∏ –µ—Å—Ç—å –∞–∫—Ç–∏–≤–Ω—ã–π –ø–æ–∏—Å–∫, –æ–±–Ω–æ–≤–ª—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å –Ω–æ–≤—ã–º–∏ —Ñ–∏–ª—å—Ç—Ä–∞–º–∏
        if (product.hasActiveSearch && searchQuery.trim()) {
            product.searchProducts(searchQuery.trim(), newFilters);
        }
    };

    // –ü—Ä–∏–º–µ–Ω–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä—ã
    const applyFilters = async () => {
        if (searchQuery.trim()) {
            await product.searchProducts(searchQuery.trim(), filters);
        } else {
            await product.fetchProducts(filters);
        }
    };

    const hasActiveFilters = filters.minPrice || filters.maxPrice || filters.sortBy !== 'newest';

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">
                    {product.selectedCity 
                        ? `–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ ${product.selectedCity}`
                        : '–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤'
                    }
                </h2>
                <div className="search-subtitle">
                    {product.selectedCity 
