// components/SearchBar.js
import React, { useState, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        minPrice: '',
        maxPrice: '',
        sortBy: 'newest', // newest, price_asc, price_desc
    });

    // Поиск товаров
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // Если поисковый запрос пустой, сбрасываем поиск
            await product.clearSearch();
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), filters);
        } catch (error) {
            console.error('Ошибка при выполнении поиска:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // Очистка поиска
    const clearSearch = async () => {
        setSearchQuery('');
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest'
        });
        await product.clearSearch();
    };

    // Очистка фильтров
    const clearFilters = async () => {
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest'
        });
        await product.fetchProducts();
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    // Обработчик изменения фильтров
    const handleFilterChange = (filterType, value) => {
        const newFilters = {
            ...filters,
            [filterType]: value
        };
        setFilters(newFilters);

        // Если есть активный поиск, обновляем результаты с новыми фильтрами
        if (product.hasActiveSearch && searchQuery.trim()) {
            product.searchProducts(searchQuery.trim(), newFilters);
        }
    };

    // Применить фильтры
    const applyFilters = async () => {
        if (searchQuery.trim()) {
            await product.searchProducts(searchQuery.trim(), filters);
        } else {
            await product.fetchProducts(filters);
        }
    };

    const hasActiveFilters = filters.minPrice || filters.maxPrice || filters.sortBy !== 'newest';

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">
                    {product.selectedCity 
                        ? `Поиск товаров в ${product.selectedCity}`
                        : 'Поиск товаров'
                    }
                </h2>
                <div className="search-subtitle">
                    {product.selectedCity 
                        ? `Ищите по названию, описанию или цене`
                        : 'Выберите город для поиска товаров'
                    }
                </div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">🔍</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder={
                            product.selectedCity 
                                ? `Поиск в ${product.selectedCity}...`
                                : 'Сначала выберите город...'
                        }
                        className="search-input"
                        disabled={isSearching || !product.selectedCity}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ✕
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching || !product.selectedCity || !searchQuery.trim()}
                    >
                        {isSearching ? (
                            <div className="search-spinner"></div>
                        ) : (
                            'Найти'
                        )}
                    </button>
                </div>
            </form>

            {/* Кнопка показа/скрытия фильтров */}
            <div className="filters-toggle-section">
                <button
                    onClick={() => setShowFilters(!showFilters)}
                    className="filters-toggle-button"
                >
                    {showFilters ? '▲ Скрыть фильтры' : '▼ Показать фильтры'}
                </button>
                {hasActiveFilters && (
                    <button
                        onClick={clearFilters}
                        className="clear-filters-button"
                    >
                        ✕ Сбросить фильтры
                    </button>
                )}
            </div>

            {/* Секция фильтров */}
            {showFilters && (
                <div className="filters-section">
                    <div className="filters-grid">
                        {/* Фильтр по минимальной цене */}
                        <div className="filter-group">
                            <label className="filter-label">Минимальная цена</label>
                            <div className="price-input-group">
                                <input
                                    type="number"
                                    value={filters.minPrice}
                                    onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                                    placeholder="0"
                                    className="price-input"
                                />
                                <span className="currency-symbol">₽</span>
                            </div>
                        </div>

                        {/* Фильтр по максимальной цене */}
                        <div className="filter-group">
                            <label className="filter-label">Максимальная цена</label>
                            <div className="price-input-group">
                                <input
                                    type="number"
                                    value={filters.maxPrice}
                                    onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                                    placeholder="∞"
                                    className="price-input"
                                />
                                <span className="currency-symbol">₽</span>
                            </div>
                        </div>

                        {/* Сортировка */}
                        <div className="filter-group">
                            <label className="filter-label">Сортировка</label>
                            <select
                                value={filters.sortBy}
                                onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                                className="filter-select"
                            >
                                <option value="newest">Сначала новые</option>
                                <option value="price_asc">Цена по возрастанию</option>
                                <option value="price_desc">Цена по убыванию</option>
                            </select>
                        </div>

                        {/* Кнопка применения фильтров */}
                        <div className="filter-actions">
                            <button
                                onClick={applyFilters}
                                className="apply-filters-button"
                                disabled={isSearching}
                            >
                                Применить фильтры
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Информация о текущем поиске */}
            {product.hasActiveSearch && (
                <div className="search-results-info">
                    <div className="results-main">
                        <div className="results-count">
                            Найдено: <strong>{product.totalCount}</strong> товаров
                        </div>
                        <div className="search-context">
                            в <strong>{product.selectedCity}</strong>
                        </div>
                    </div>
                    <div className="search-query">
                        По запросу: "<strong>{product.currentSearchQuery}</strong>"
                    </div>
                    {hasActiveFilters && (
                        <div className="active-filters">
                            {filters.minPrice && (
                                <span className="active-filter">От {filters.minPrice}₽</span>
                            )}
                            {filters.maxPrice && (
                                <span className="active-filter">До {filters.maxPrice}₽</span>
                            )}
                            {filters.sortBy !== 'newest' && (
                                <span className="active-filter">
                                    {filters.sortBy === 'price_asc' ? 'По возрастанию цены' : 'По убыванию цены'}
                                </span>
                            )}
                        </div>
                    )}
                    <button 
                        onClick={clearSearch}
                        className="clear-search-link"
                    >
                        Сбросить поиск
                    </button>
                </div>
            )}

            {/* Сообщение если город не выбран */}
            {!product.selectedCity && (
                <div className="no-city-message">
                    <div className="no-city-icon">📍</div>
                    <div className="no-city-text">
                        <strong>Выберите город выше</strong> для поиска товаров
                    </div>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
