import React, { useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductList from "../components/ProductList"; 
import Pages from '../components/Pages';
import { observer } from "mobx-react-lite";
import { productStore } from '../store/ProductStore';
import { Context } from '../index';

const Shop = () => {
    const { product } = useContext(Context);

    useEffect(() => {
        const city = sessionStorage.getItem('city') || ''; // Получаем город из sessionStorage
        if (city) {
            product.fetchProductsByCity(city); // Загружаем продукты для этого города
        } else {
            product.fetchProducts(); // Загружаем все продукты, если город не выбран
        }
    }, [product.page]); // Загрузка продуктов при изменении страницы

    return (
        <Container>
            <Row>
                <Col md={9}>
                    <ProductList />
                    <Pages />
                </Col>
            </Row>
        </Container>
    );
};

export default observer(Shop);
