// components/ProductItem.js
import React from 'react';
import { Card, Col } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useHistory } from 'react-router-dom';
import { PRODUCT_ROUTE } from '../utils/const'; // Убрал 's'
import { observer } from 'mobx-react-lite';
import '../css/components/ProductItem.css';
const ProductItem = ({ product }) => {
    const history = useHistory();

    return (
        <Col md={3} className="mb-4" onClick={() => history.push(PRODUCT_ROUTE + '/' + product.id)}>
            <Card 
                style={{ width: '100%', cursor: 'pointer', transition: 'transform 0.2s' }} 
                border="light"
                className="h-100"
                onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.02)'}
                onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
            >
                <Image 
                    width="100%" 
                    height={200} 
                    src={process.env.REACT_APP_API_URL + product.img} 
                    style={{ objectFit: 'cover' }}
                />
                <Card.Body className="d-flex flex-column">
                    <Card.Title className="flex-grow-1" style={{ fontSize: '1.1rem' }}>
                        {product.name}
                    </Card.Title>
                    <div className="mt-auto">
                        <div className="d-flex justify-content-between align-items-center">
                            <span className="text-primary fw-bold" style={{ fontSize: '1.2rem' }}>
                                {product.price} $
                            </span>
                            <small className="text-muted">{product.city}</small>
                        </div>
                    </div>
                </Card.Body>
            </Card>
        </Col>
    );
};

export default observer(ProductItem);
