// src/pages/UserProfilePage.js

import React, { useEffect, useState } from 'react';
import { getUserInfo, getSellerInfo } from '../http/userAPI'; // Импортируйте функции API

const UserInfo = () => {
    const [user, setUser] = useState(null);
    const [seller, setSeller] = useState(null);
    const userId = /* Получите ID текущего пользователя, например, из localStorage или контекста */

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userData = await getUserInfo(userId);
                setUser(userData);
                
                if (userData.role === 'SELLER') { // Предположим, что роль SELLER указывает, что это продавец
                    const sellerData = await getSellerInfo(userData.id); // Получение информации о продавце
                    setSeller(sellerData);
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
            }
        };

        fetchData();
    }, [userId]);

    if (!user) {
        return <div>Загрузка...</div>;
    }

    return (
        <div>
            <h1>Информация о пользователе</h1>
            <p>Имя: {user.username}</p>
            <p>Email: {user.email}</p>
            <p>Телефон: {user.phone}</p>

            {seller && (
                <div>
                    <h2>Информация о продавце</h2>
                    <p>Имя: {seller.name}</p>
                    <p>Описание: {seller.description}</p>
                    <img src={seller.img} alt={seller.name} />
                </div>
            )}
        </div>
    );
};

export default UserInfo;
