import React, { useEffect, useState } from 'react';
import geo from '../assets/geo.png'; 
import CitySelectorModal from '..components/modals/CitySelector'; // Импортируем модальное окно

const cities = [
    "Минск",
    "Брест",
    "Гомель",
    "Гродно",
    "Могилев",
    "Витебск",
    "Пинск",
    "Барановичи",
    "Бобруйск",
    "Слонім",
    "Лида",
    "Орша",
    "Мозырь",
    "Слуцк",
    "Новополоцк",
    "Светлогорск",
    "Жлобин"
];

const CityDisplay = () => {
    const [city, setCity] = useState(''); 
    const [selectedCity, setSelectedCity] = useState('');
    const [showModal, setShowModal] = useState(false); // Состояние для управления модальным окном

    useEffect(() => {
        const getCity = async () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(async (position) => {
                    const { latitude, longitude } = position.coords;
                    const response = await fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`);
                    const data = await response.json();
                    const currentCity = data.locality || 'Неизвестный город';
                    setCity(currentCity); 
                    setSelectedCity(currentCity); // Устанавливаем текущее местоположение как выбранный город
                    sessionStorage.setItem('city', currentCity); 
                });
            }
        };

        getCity();
    }, []);

    const handleCityChange = (e) => {
        const newCity = e.target.value;
        setSelectedCity(newCity);
        setCity(newCity); // Обновляем текущий город на выбранный
        sessionStorage.setItem('city', newCity); // Сохраняем выбранный город
    };

    const handleOpenModal = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    return (
        <div>
            <img 
                src={geo} 
                style={{ width: '40px', height: '40px', cursor: 'pointer' }} 
                alt="Геолокация" 
                onClick={handleOpenModal} // Открытие модального окна по клику на изображение
            />
            <h5 onClick={handleOpenModal} style={{ cursor: 'pointer' }}>{city}</h5> {/* Открытие модального окна по клику на название города */}
            <CitySelectorModal 
                show={showModal} 
                handleClose={handleCloseModal} 
                cities={cities} 
                selectedCity={selectedCity} 
                handleCityChange={handleCityChange} 
            />
        </div>
    );
};

export default CityDisplay;
