import React, { useEffect, useState, useContext } from 'react';
import { getUserInfo, getSellerInfo } from '../http/userAPI';
import { Context } from '../index';

const UserInfo = () => {
    const { user } = useContext(Context);
    const [seller, setSeller] = useState(null);
    const [userData, setUserData] = useState(null);
    const userId = user.user.id;

    console.log('Данные пользователя:', user.user);
console.log('Email пользователя:', user.email);
console.log('Роль пользователя:', user.role);
    useEffect(() => {
        const fetchData = async () => {
            if (!userId) return;

            try {
                const userInfo = await getUserInfo(userId);
                setUserData(userInfo);

                const sellerData = await getSellerInfo(userInfo.seller_id);
                setSeller(sellerData);
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
            }
        };

        fetchData();
    }, [userId]);

    if (!user || Object.keys(user).length === 0) {
        return <div>Загрузка...</div>;
    }

    return (
        <div>
            <h1>Информация о пользователе</h1>
            <p>Имя: {user.username}</p>
            <p>Email: {user.email}</p>
            <p>Телефон: {user.phone}</p>

            {seller && (
                <div>
                    <h2>Информация о продавце</h2>
                    <p>Имя: {seller.name}</p>
                    <p>Описание: {seller.description}</p>
                    <img src={seller.img} alt={seller.name} />
                </div>
            )}
        </div>
    );
};

export default UserInfo;
