import React, { useContext } from "react";
import { Context } from "../index";
import {useHistory} from "react-router-dom"
import { observer } from "mobx-react-lite";
import { Nav, Navbar, Container, Button } from 'react-bootstrap';
import { LOGIN_ROUTE, ADMIN_ROUTE } from "../utils/const";
import logo from '../assets/logo.png'; 
import favorite from '../assets/Favorite.png'; 
import CityDisplay from './CityDisplay'; 

const NavBar = () => {
    const { user } = useContext(Context);
    const history = useHistory()

    const logOut=()=>{
        user.setUser({})
        user.setIsAuth(false)
    }

    return (
        <Navbar collapseOnSelect expand="lg" style={{ backgroundColor: '#ffff00' }}>
            <Container>
                    <img 
                        src={logo} 
                        alt="Logo" 
                        style={{ width: '50px', height: '50px', marginRight: '8px' }} 
                    />
                     <img 
                        src={favorite} 
                       
                        style={{ width: '50px', height: '50px', marginRight: '38px' }} 
                    />
                    
                <Navbar.Brand href="/home">My Bag</Navbar.Brand>
                <Navbar.Toggle aria-contr ols="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="me-auto">
                        <CityDisplay /> 
                    </Nav>
                    {user.isAuth ? (
                        <Nav>
                            <Button variant="outline-dark" 
                            onClick={()=>history.push(ADMIN_ROUTE)}
                            >Админ панель
                            </Button>
                            <Button variant="outline-dark" 
                            onClick={()=>logOut()}
                            >Выйти
                            </Button>
                        </Nav>
                    ) : (
                        <Nav>
                            <Button variant="outline-dark" onClick={() => history.push(LOGIN_ROUTE)}>
                                Авторизоваться
                            </Button>
                        </Nav>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default observer(NavBar);
