// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [categories, setCategories] = useState([]);
    const [characteristics, setCharacteristics] = useState([]);
   const [filters, setFilters] = useState({
    minPrice: '',
    maxPrice: '',
    sortBy: 'newest',
    categoryId: '',
    characteristics: {},
    excludeNoPrice: false // Добавьте это
});
    const [errors, setErrors] = useState({});

    // Загрузка категорий при монтировании
    useEffect(() => {
        loadCategories();
    }, []);

    // Загрузка категорий
    const loadCategories = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/prod/types');
            if (response.ok) {
                const data = await response.json();
                setCategories(data);
            }
        } catch (error) {
            console.error('Ошибка при загрузке категорий:', error);
        }
    };

    // Загрузка характеристик для выбранной категории
    useEffect(() => {
        if (filters.categoryId) {
            loadCharacteristics(filters.categoryId);
        } else {
            setCharacteristics([]);
            setFilters(prev => ({ ...prev, characteristics: {} }));
        }
    }, [filters.categoryId]);

    const loadCharacteristics = async (categoryId) => {
        try {
            // Загружаем товары категории чтобы извлечь характеристики
            const response = await fetch(`http://localhost:5000/api/prod?typeId=${categoryId}&limit=50`);
            if (response.ok) {
                const data = await response.json();
                const products = data.rows || data;
                
                // Извлекаем уникальные характеристики
                const uniqueCharacteristics = {};
                products.forEach(product => {
                    if (product.info && Array.isArray(product.info)) {
                        product.info.forEach(info => {
                            if (!uniqueCharacteristics[info.title]) {
                                uniqueCharacteristics[info.title] = new Set();
                            }
                            uniqueCharacteristics[info.title].add(info.description);
                        });
                    }
                });

                // Преобразуем в массив для отображения
                const charArray = Object.entries(uniqueCharacteristics).map(([title, values]) => ({
                    title,
                    values: Array.from(values)
                }));

                setCharacteristics(charArray);
            }
        } catch (error) {
            console.error('Ошибка при загрузке характеристик:', error);
        }
    };

    // Валидация фильтров
    const validateFilters = () => {
        const newErrors = {};

        if (filters.minPrice && (isNaN(filters.minPrice) || parseFloat(filters.minPrice) < 0)) {
            newErrors.minPrice = 'Минимальная цена должна быть положительным числом';
        }

        if (filters.maxPrice && (isNaN(filters.maxPrice) || parseFloat(filters.maxPrice) < 0)) {
            newErrors.maxPrice = 'Максимальная цена должна быть положительным числом';
        }

        if (filters.minPrice && filters.maxPrice) {
            const min = parseFloat(filters.minPrice);
            const max = parseFloat(filters.maxPrice);
            if (min > max) {
                newErrors.priceRange = 'Минимальная цена не может быть больше максимальной';
            }
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    // Поиск товаров
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            await product.clearSearch();
            return;
        }

        if (!validateFilters()) {
            return;
        }

        setIsSearching(true);
        try {
            await product.searchProducts(searchQuery.trim(), filters);
        } catch (error) {
            console.error('Ошибка при выполнении поиска:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // Очистка поиска
    const clearSearch = async () => {
        setSearchQuery('');
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest',
            categoryId: '',
            characteristics: {}
        });
        setErrors({});
        await product.clearSearch();
    };

    // Очистка фильтров
    const clearFilters = async () => {
        setFilters({
            minPrice: '',
            maxPrice: '',
            sortBy: 'newest',
            categoryId: '',
            characteristics: {}
        });
        setErrors({});
        setCharacteristics([]);
        await product.fetchProducts();
    };

    // Очистка ошибки
    const clearError = (fieldName) => {
        setErrors(prev => {
            const newErrors = { ...prev };
            delete newErrors[fieldName];
            return newErrors;
        });
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    // Обработчик изменения фильтров
    const handleFilterChange = (filterType, value) => {
        const newFilters = {
            ...filters,
            [filterType]: value
        };
        setFilters(newFilters);

        if (errors[filterType]) {
            clearError(filterType);
        }
        if (errors.priceRange && (filterType === 'minPrice' || filterType === 'maxPrice')) {
            clearError('priceRange');
        }

        if (validateFilters()) {
            if (searchQuery.trim()) {
                product.searchProducts(searchQuery.trim(), newFilters);
            } else {
                product.fetchProducts(newFilters);
            }
        }
    };

    // Обработчик изменения характеристик
    const handleCharacteristicChange = (charTitle, charValue, checked) => {
        const newCharacteristics = { ...filters.characteristics };
        
        if (checked) {
            if (!newCharacteristics[charTitle]) {
                newCharacteristics[charTitle] = [];
            }
            newCharacteristics[charTitle].push(charValue);
        } else {
            if (newCharacteristics[charTitle]) {
                newCharacteristics[charTitle] = newCharacteristics[charTitle].filter(v => v !== charValue);
                if (newCharacteristics[charTitle].length === 0) {
                    delete newCharacteristics[charTitle];
                }
            }
        }

        const newFilters = {
            ...filters,
            characteristics: newCharacteristics
        };
        setFilters(newFilters);

        if (validateFilters()) {
            if (searchQuery.trim()) {
                product.searchProducts(searchQuery.trim(), newFilters);
            } else {
                product.fetchProducts(newFilters);
            }
        }
    };

    // Очистка характеристики
    const clearCharacteristic = (charTitle) => {
        const newCharacteristics = { ...filters.characteristics };
        delete newCharacteristics[charTitle];
        
        const newFilters = {
            ...filters,
            characteristics: newCharacteristics
        };
        setFilters(newFilters);

        if (validateFilters()) {
            if (searchQuery.trim()) {
                product.searchProducts(searchQuery.trim(), newFilters);
            } else {
                product.fetchProducts(newFilters);
            }
        }
    };

    // Применить фильтры
    const applyFilters = async () => {
        if (!validateFilters()) {
            return;
        }

        if (searchQuery.trim()) {
            await product.searchProducts(searchQuery.trim(), filters);
        } else {
            await product.fetchProducts(filters);
        }
    };

    const hasActiveFilters = filters.minPrice || filters.maxPrice || filters.sortBy !== 'newest' || 
                            filters.categoryId || Object.keys(filters.characteristics).length > 0;

    const hasActiveCharacteristics = Object.keys(filters.characteristics).length > 0;

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">
                    {product.selectedCity 
                        ? `Поиск товаров в ${product.selectedCity}`
                        : 'Поиск товаров'
                    }
                </h2>
                <div className="search-subtitle">
                    {product.selectedCity 
                        ? `Ищите по названию, описанию или цене`
                        : 'Выберите город для поиска товаров'
                    }
                </div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">🔍</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder={
                            product.selectedCity 
                                ? `Поиск в ${product.selectedCity}...`
                                : 'Сначала выберите город...'
                        }
                        className="search-input"
                        disabled={isSearching || !product.selectedCity}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ✕
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching || !product.selectedCity || !searchQuery.trim()}
                    >
                        {isSearching ? (
                            <div className="search-spinner"></div>
                        ) : (
                            'Найти'
                        )}
                    </button>
                </div>
            </form>

            {/* Кнопка показа/скрытия фильтров */}
            <div className="filters-toggle-section">
                <button
                    onClick={() => setShowFilters(!showFilters)}
                    className="filters-toggle-button"
                >
                    {showFilters ? '▲ Скрыть фильтры' : '▼ Показать фильтры'}
                </button>
                {hasActiveFilters && (
                    <button
                        onClick={clearFilters}
                        className="clear-filters-button"
                    >
                        ✕ Сбросить фильтры
                    </button>
                )}
            </div>

            {/* Секция фильтров */}
       // В компоненте SearchBar замените структуру фильтров на:
{showFilters && (
    <div className="filters-sidebar-layout">
        {/* Боковая панель фильтров */}
        <div className="filters-section">
            <div className="filters-header">
                <h3 className="filters-title">Фильтры</h3>
                {hasActiveFilters && (
                    <button
                        onClick={clearFilters}
                        className="clear-filters-button"
                    >
                        Сбросить
                    </button>
                )}
            </div>

            {errors.priceRange && (
                <div className="filter-error general-error">
                    ⚠️ {errors.priceRange}
                </div>
            )}

            <div className="filters-vertical">
                {/* Фильтр по категории */}
                <div className="filter-group">
                    <label className="filter-label">Категория</label>
                    <select
                        value={filters.categoryId}
                        onChange={(e) => handleFilterChange('categoryId', e.target.value)}
                        className="filter-select"
                    >
                        <option value="">Все категории</option>
                        {categories.map(category => (
                            <option key={category.id} value={category.id}>
                                {category.name}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Фильтр по цене */}
                <div className="filter-group">
                    <label className="filter-label">Цена</label>
                    <div className="price-input-group">
                        <input
                            type="number"
                            value={filters.minPrice}
                            onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                            placeholder="От"
                            className={`price-input ${errors.minPrice ? 'error' : ''}`}
                            min="0"
                            step="0.01"
                        />
                        <span className="currency-symbol">₽</span>
                    </div>
                    {errors.minPrice && (
                        <div className="filter-error">⚠️ {errors.minPrice}</div>
                    )}
                    
                    <div className="price-input-group" style={{marginTop: '8px'}}>
                        <input
                            type="number"
                            value={filters.maxPrice}
                            onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                            placeholder="До"
                            className={`price-input ${errors.maxPrice ? 'error' : ''}`}
                            min="0"
                            step="0.01"
                        />
                        <span className="currency-symbol">₽</span>
                    </div>
                    {errors.maxPrice && (
                        <div className="filter-error">⚠️ {errors.maxPrice}</div>
                    )}
                </div>

                {/* Сортировка */}
                <div className="filter-group">
                    <label className="filter-label">Сортировка</label>
                    <select
                        value={filters.sortBy}
                        onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                        className="filter-select"
                    >
                        <option value="newest">Сначала новые</option>
                        <option value="price_asc">Цена по возрастанию</option>
                        <option value="price_desc">Цена по убыванию</option>
                        <option value="name_asc">По названию (А-Я)</option>
                        <option value="name_desc">По названию (Я-А)</option>
                    </select>
                </div>

                {/* Характеристики */}
                {characteristics.length > 0 && (
                    <div className="characteristics-section">
                        <h4 className="characteristics-title">Характеристики</h4>
                        <div className="characteristics-vertical">
                            {characteristics.map(char => (
                                <div key={char.title} className="characteristic-group">
                                    <label className="characteristic-label">{char.title}</label>
                                    <div className="characteristic-values">
                                        {char.values.map(value => (
                                            <label key={value} className="characteristic-value">
                                                <input
                                                    type="checkbox"
                                                    checked={filters.characteristics[char.title]?.includes(value) || false}
                                                    onChange={(e) => handleCharacteristicChange(char.title, value, e.target.checked)}
                                                    className="characteristic-checkbox"
                                                />
                                                <span className="characteristic-text">{value}</span>
                                            </label>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Кнопка применения фильтров */}
                <div className="filter-actions-full">
                    <button
                        onClick={applyFilters}
                        className="apply-filters-button"
                        disabled={isSearching || Object.keys(errors).length > 0}
                    >
                        Применить фильтры
                    </button>
                </div>
            </div>
        </div>

        {/* Контентная область */}
        <div className="content-area">
            {/* Здесь будет ваш контент - результаты поиска, товары и т.д. */}
            {children}
        </div>
    </div>
)}

            {/* Информация о текущем поиске */}
            {product.hasActiveSearch && (
                <div className="search-results-info">
                    <div className="results-main">
                        <div className="results-count">
                            Найдено: <strong>{product.totalCount}</strong> товаров
                        </div>
                        <div className="search-context">
                            в <strong>{product.selectedCity}</strong>
                        </div>
                    </div>
                    <div className="search-query">
                        По запросу: "<strong>{product.currentSearchQuery}</strong>"
                    </div>
                    {hasActiveFilters && (
                        <div className="active-filters">
                            {filters.categoryId && (
                                <span className="active-filter">
                                    Категория: {categories.find(c => c.id == filters.categoryId)?.name}
                                    <button 
                                        onClick={() => handleFilterChange('categoryId', '')}
                                        className="active-filter-remove"
                                    >
                                        ✕
                                    </button>
                                </span>
                            )}
                            {filters.minPrice && (
                                <span className="active-filter">
                                    От {filters.minPrice}₽
                                    <button 
                                        onClick={() => handleFilterChange('minPrice', '')}
                                        className="active-filter-remove"
                                    >
                                        ✕
                                    </button>
                                </span>
                            )}
                            {filters.maxPrice && (
                                <span className="active-filter">
                                    До {filters.maxPrice}₽
                                    <button 
                                        onClick={() => handleFilterChange('maxPrice', '')}
                                        className="active-filter-remove"
                                    >
                                        ✕
                                    </button>
                                </span>
                            )}
                            {filters.sortBy !== 'newest' && (
                                <span className="active-filter">
                                    {filters.sortBy === 'price_asc' && 'По возрастанию цены'}
                                    {filters.sortBy === 'price_desc' && 'По убыванию цены'}
                                    {filters.sortBy === 'name_asc' && 'По названию (А-Я)'}
                                    {filters.sortBy === 'name_desc' && 'По названию (Я-А)'}
                                    <button 
                                        onClick={() => handleFilterChange('sortBy', 'newest')}
                                        className="active-filter-remove"
                                    >
                                        ✕
                                    </button>
                                </span>
                            )}
                            {hasActiveCharacteristics && Object.entries(filters.characteristics).map(([title, values]) => (
                                <span key={title} className="active-filter">
                                    {title}: {values.join(', ')}
                                    <button 
                                        onClick={() => clearCharacteristic(title)}
                                        className="active-filter-remove"
                                    >
                                        ✕
                                    </button>
                                </span>
                            ))}
                        </div>
                    )}
                    <button 
                        onClick={clearSearch}
                        className="clear-search-link"
                    >
                        Сбросить поиск
                    </button>
                </div>
            )}

            {/* Сообщение если город не выбран */}
            {!product.selectedCity && (
                <div className="no-city-message">
                    <div className="no-city-icon">📍</div>
                    <div className="no-city-text">
                        <strong>Выберите город выше</strong> для поиска товаров
                    </div>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
