// pages/ProductPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { fetchOneProduct } from '../http/productAPI';
import { observer } from 'mobx-react-lite';
import '../css/pages/ProductPage.css'; // Убедитесь, что путь правильный

const ProductPage = observer(() => {
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        const getProduct = async () => {
            try {
                setLoading(true);
                const productData = await fetchOneProduct(id);
                setProduct(productData);
            } catch (e) {
                setError('Ошибка при загрузке товара');
                console.error('Error fetching product:', e);
            } finally {
                setLoading(false);
            }
        };

        getProduct();
    }, [id]);

    if (loading) {
        return (
            <div className="product-page-container">
                <div className="loading-state">
                    <div className="loading-spinner">Загрузка...</div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="product-page-container">
                <div className="error-state">
                    <div className="alert alert-danger">{error}</div>
                </div>
            </div>
        );
    }

    if (!product) {
        return (
            <div className="product-page-container">
                <div className="not-found-state">
                    <div className="alert alert-warning">Товар не найден</div>
                </div>
            </div>
        );
    }

    return (
        <div className="product-page-container">
            <div className="container">
                <button className="back-button" onClick={() => history.goBack()}>
                    ← Назад
                </button>
                
                <div className="product-content">
                    <div className="product-image-section">
                        <div className="product-image-card">
                            <img 
                                src={process.env.REACT_APP_API_URL + product.img}
                                alt={product.name}
                                className="product-image"
                            />
                        </div>
                    </div>
                    
                    <div className="product-info-section">
                        <div className="product-info-card">
                            <h1 className="product-title">{product.name}</h1>
                            <div className="product-price">{product.price} $</div>
                            
                            <div className="info-section">
                                <div className="info-label">Город</div>
                                <div className="info-text">{product.city}</div>
                            </div>
                            
                            {product.description && (
                                <div className="info-section">
                                    <div className="info-label">Описание</div>
                                    <p className="info-text">{product.description}</p>
                                </div>
                            )}

                            {product.info && product.info.length > 0 && (
                                <div className="characteristics-section">
                                    <div className="info-label">Характеристики</div>
                                    {product.info.map((infoItem, index) => (
                                        <div key={index} className="characteristic-item">
                                            <span className="characteristic-title">{infoItem.title}:</span>
                                            <span className="characteristic-description">{infoItem.description}</span>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <button className="contact-button">
                                Связаться с продавцом
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
});

export default ProductPage;
