// components/modals/CitySelectorWithYandexMap.js
import React, { useState, useEffect } from 'react';
import { Modal, Button, Tabs, Tab, Spinner, Form } from 'react-bootstrap';

const CitySelectorModals = ({ 
    show, 
    handleClose, 
    cities, 
    selectedCity, 
    handleCityChange,
    onDetectLocation,
    isLoading 
}) => {
    const [activeTab, setActiveTab] = useState('list');
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredCities, setFilteredCities] = useState([]);
    const [map, setMap] = useState(null);
    const [mapLoaded, setMapLoaded] = useState(false);

    // –ó–∞–≥—Ä—É–∑–∫–∞ –Ø–Ω–¥–µ–∫—Å.–ö–∞—Ä—Ç
    useEffect(() => {
        if (show && activeTab === 'map' && !mapLoaded) {
            const script = document.createElement('script');
            script.src = 'https://api-maps.yandex.ru/2.1/?lang=ru_RU';
            script.async = true;
            script.onload = () => {
                window.ymaps.ready(() => {
                    initMap();
                    setMapLoaded(true);
                });
            };
            document.head.appendChild(script);

            return () => {
                // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
                if (map) {
                    map.destroy();
                }
            };
        }
    }, [show, activeTab]);

    const initMap = () => {
        const yandexMap = new window.ymaps.Map('yandex-map', {
            center: [53.9, 27.5], // –¶–µ–Ω—Ç—Ä –ë–µ–ª–∞—Ä—É—Å–∏
            zoom: 6,
            controls: ['zoomControl', 'fullscreenControl']
        });

        // –î–æ–±–∞–≤–ª—è–µ–º –ø–æ–∏—Å–∫
        const searchControl = new window.ymaps.control.SearchControl({
            options: {
                provider: 'yandex#search',
                noPlacemark: true
            }
        });
        yandexMap.controls.add(searchControl);

        // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤—ã–±–æ—Ä–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –ø–æ–∏—Å–∫–∞
        searchControl.events.add('resultselect', function (e) {
            const results = searchControl.getResultsArray();
            const selectedResult = results[e.get('index')];
            const cityName = extractCityName(selectedResult);
            
            if (cityName && cities.includes(cityName)) {
                handleCitySelection(cityName);
            }
        });

        // –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–∫–∏ –¥–ª—è –≥–æ—Ä–æ–¥–æ–≤
        cities.forEach(city => {
            // –ü–æ–ª—É—á–∞–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≥–æ—Ä–æ–¥–∞ (—É–ø—Ä–æ—â–µ–Ω–Ω–æ)
            const coords = getCityCoordinates(city);
            if (coords) {
                const placemark = new window.ymaps.Placemark(coords, {
                    balloonContent: city,
                    hintContent: city
                }, {
                    preset: selectedCity === city ? 'islands#blueIcon' : 'islands#redIcon'
                });

                placemark.events.add('click', () => {
                    handleCitySelection(city);
                });

                yandexMap.geoObjects.add(placemark);
            }
        });

        setMap(yandexMap);
    };

    const getCityCoordinates = (city) => {
        // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –æ—Å–Ω–æ–≤–Ω—ã—Ö –≥–æ—Ä–æ–¥–æ–≤ –ë–µ–ª–∞—Ä—É—Å–∏
        const coordinates = {
            '–ú–∏–Ω—Å–∫': [53.9, 27.5],
            '–ë—Ä–µ—Å—Ç': [52.1, 23.7],
            '–ì—Ä–æ–¥–Ω–æ': [53.7, 23.8],
            '–ì–æ–º–µ–ª—å': [52.4, 31.0],
            '–í–∏—Ç–µ–±—Å–∫': [55.2, 30.2],
            '–ú–æ–≥–∏–ª—ë–≤': [53.9, 30.3],
            '–ë–∞—Ä–∞–Ω–æ–≤–∏—á–∏': [53.1, 26.0],
            '–ë–æ—Ä–∏—Å–æ–≤': [54.2, 28.5],
            '–ü–∏–Ω—Å–∫': [52.1, 26.1],
            '–û—Ä—à–∞': [54.5, 30.4]
        };
        return coordinates[city];
    };

    const extractCityName = (result) => {
        const name = result.properties.get('name');
        // –ü—ã—Ç–∞–µ–º—Å—è –Ω–∞–π—Ç–∏ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ —Å –Ω–∞—à–∏–º —Å–ø–∏—Å–∫–æ–º –≥–æ—Ä–æ–¥–æ–≤
        return cities.find(city => name.includes(city));
    };

    const handleCitySelection = (cityName) => {
        const mockEvent = {
            target: { value: cityName }
        };
        handleCityChange(mockEvent);
    };

    // ... –æ—Å—Ç–∞–ª—å–Ω–æ–π –∫–æ–¥ —Ç–∞–∫–æ–π –∂–µ –∫–∞–∫ –≤ –ø–µ—Ä–≤–æ–º –≤–∞—Ä–∏–∞–Ω—Ç–µ ...

    return (
        <Modal show={show} onHide={handleClose} size="xl" style={{maxWidth: '90%'}}>
            <Modal.Header closeButton>
                <Modal.Title>–í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥</Modal.Title>
            </Modal.Header>
            <Modal.Body style={{padding: '20px'}}>
                <Tabs activeKey={activeTab} onSelect={handleTabSelect} className="mb-3">
                    {/* –í–∫–ª–∞–¥–∫–∞ —Å–æ —Å–ø–∏—Å–∫–æ–º (–æ—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –≤ –ø–µ—Ä–≤–æ–º –≤–∞—Ä–∏–∞–Ω—Ç–µ) */}
                    
                    <Tab eventKey="map" title="üó∫Ô∏è –ò–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω–∞—è –∫–∞—Ä—Ç–∞">
                        <div style={{ height: '500px', position: 'relative' }}>
                            <div 
                                id="yandex-map" 
                                style={{ 
                                    width: '100%', 
                                    height: '100%',
                                    borderRadius: '8px'
                                }}
                            >
                                {!mapLoaded && (
                                    <div style={{
                                        position: 'absolute',
                                        top: '50%',
                                        left: '50%',
                                        transform: 'translate(-50%, -50%)',
                                        textAlign: 'center'
                                    }}>
                                        <Spinner animation="border" />
                                        <div className="mt-2">–ó–∞–≥—Ä—É–∑–∫–∞ –∫–∞—Ä—Ç—ã...</div>
                                    </div>
                                )}
                            </div>
                        </div>
                        <div className="mt-3">
                            <small className="text-muted">
                                üí° <strong>–ò–Ω—Å—Ç—Ä—É–∫—Ü–∏—è:</strong> –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –ø–æ–∏—Å–∫ –∏–ª–∏ –∫–ª–∏–∫–Ω–∏—Ç–µ –Ω–∞ –º–µ—Ç–∫—É –≥–æ—Ä–æ–¥–∞
                            </small>
                        </div>
                    </Tab>
                </Tabs>
            </Modal.Body>
            {/* –§—É—Ç–µ—Ä –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ */}
        </Modal>
    );
};

export default CitySelectorModals;
