import React, { useContext, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductList from "../components/ProductList"; // Импортируйте компонент для отображения продуктов
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { fetchProductBrands, fetchProducts, fetchProductTypes } from "../http/productAPI"; // Импортируйте ваши API функции
import Pages from "../components/Pages";

const Shop = () => {
    const { product } = useContext(Context);

    useEffect(() => {
         fetchProducts(null, null, 1, 2).then(data => {
            product.setProducts(data.rows); // Установка продуктов
            product.setTotalCount(data.count); // Установка общего количества
        });
    }, []);

     return (
        <Container>
            <Row>
              
                <Col md={9}>
                    <ProductList /> {/* Компонент для отображения списка продуктов */}
                    <Pages /> {/* Компонент для постраничной навигации */}
                </Col>
            </Row>
        </Container>
    );
};

export default observer(Shop);
