import React, { useEffect, useState, useContext } from 'react';
import { getUserInfo, getSellerInfo, updateUser, updateSeller } from '../http/userAPI';
import { Context } from '../index';
import { observer } from "mobx-react-lite";
import './UserInfo.css'; // Импортируем стили

const UserInfo = () => {
    const { user } = useContext(Context);
    const [seller, setSeller] = useState(null);
    const [editingUser, setEditingUser] = useState(false);
    const [editingSeller, setEditingSeller] = useState(false);
    const [updatedUser, setUpdatedUser] = useState({ username: user.user.username, email: user.user.email, phone: user.user.phone });
    const [updatedSeller, setUpdatedSeller] = useState({ name: '', description: '', img: '' });
    const userId = user.user.id;

    useEffect(() => {
        const fetchData = async () => {
            if (!userId) return;

            try {
                const userInfo = await getUserInfo(userId);
                user.setUser(userInfo);
                if (user.user.id) {
                    const sellerData = await getSellerInfo(user.user.id);
                    setSeller(sellerData);
                    setUpdatedSeller({ name: sellerData.name, description: sellerData.description, img: sellerData.img });
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
            }
        };

        fetchData();
    }, [userId]);

    const handleUserUpdate = async (e) => {
        e.preventDefault();
        try {
            await updateUser(userId, updatedUser);
            user.setUser({ ...user.user, ...updatedUser }); 
            setEditingUser(false);
        } catch (error) {
            console.error('Ошибка при обновлении пользователя:', error);
        }
    };

    const handleSellerUpdate = async (e) => {
        e.preventDefault();
        try {
            await updateSeller(seller.id, updatedSeller);
            setSeller(updatedSeller);
            setEditingSeller(false);
        } catch (error) {
            console.error('Ошибка при обновлении продавца:', error);
        }
    };

    return (
        <div className="user-info-container">
            <h1>Информация о пользователе</h1>
            <div className="user-info">
                {!editingUser ? (
                    <div className="info-display">
                        <p><strong>Имя:</strong> {user.user.username}</p>
                        <p><strong>Email:</strong> {user.user.email}</p>
                        <p><strong>Телефон:</strong> {user.user.phone}</p>
                        <button className="edit-button" onClick={() => setEditingUser(true)}>Редактировать</button>
                    </div>
                ) : (
                    <form className="info-edit" onSubmit={handleUserUpdate}>
                        <input
                            type="text"
                            value={updatedUser.username}
                            onChange={(e) => setUpdatedUser({ ...updatedUser, username: e.target.value })}
                            placeholder="Имя"
                        />
                        <input
                            type="email"
                            value={updatedUser.email}
                            onChange={(e) => setUpdatedUser({ ...updatedUser, email: e.target.value })}
                            placeholder="Email"
                        />
                        <input
                            type="tel"
                            value={updatedUser.phone}
                            onChange={(e) => setUpdatedUser({ ...updatedUser, phone: e.target.value })}
                            placeholder="Телефон"
                        />
                        <button type="submit" className="save-button">Сохранить</button>
                        <button type="button" onClick={() => setEditingUser(false)} className="cancel-button">Отмена</button>
                    </form>
                )}
            </div>

            {seller && (
                <div className="seller-info">
                    <h2>Информация о продавце</h2>
                    {!editingSeller ? (
                        <div className="info-display">
                            <p><strong>Имя:</strong> {seller.name}</p>
                            <p><strong>Описание:</strong> {seller.description}</p>
                            {seller.img && <img src={seller.img} alt={seller.name} className="seller-image" />}
                            <button className="edit-button" onClick={() => setEditingSeller(true)}>Редактировать</button>
                        </div>
                    ) : (
                        <form className="info-edit" onSubmit={handleSellerUpdate}>
                            <input
                                type="text"
                                value={updatedSeller.name}
                                onChange={(e) => setUpdatedSeller({ ...updatedSeller, name: e.target.value })}
                                placeholder="Имя продавца"
                            />
                            <input
                                type="text"
                                value={updatedSeller.description}
                                onChange={(e) => setUpdatedSeller({ ...updatedSeller, description: e.target.value })}
                                placeholder="Описание"
                            />
                            <input
                                type="text"
                                value={updatedSeller.img}
                                onChange={(e) => setUpdatedSeller({ ...updatedSeller, img: e.target.value })}
                                placeholder="URL изображения"
                            />
                            <button type="submit" className="save-button">Сохранить</button>
                            <button type="button" onClick={() => setEditingSeller(false)} className="cancel-button">Отмена</button>
                        </form>
                    )}
                </div>
            )}
        </div>
    );
};

export default observer(UserInfo);
