// components/SearchBar.js
import React, { useState, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);

    // –ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ –≤—ã–±—Ä–∞–Ω–Ω–æ–º –≥–æ—Ä–æ–¥–µ
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // –ï—Å–ª–∏ –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å –ø—É—Å—Ç–æ–π, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å–µ —Ç–æ–≤–∞—Ä—ã –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –≥–æ—Ä–æ–¥–∞
            if (product.selectedCity) {
                await product.fetchProductsByCity(product.selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            // –í—Å–µ–≥–¥–∞ –∏—â–µ–º –≤ –≤—ã–±—Ä–∞–Ω–Ω–æ–º –≥–æ—Ä–æ–¥–µ
            await product.searchProducts(searchQuery.trim(), { city: product.selectedCity });
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // –û—á–∏—Å—Ç–∫–∞ –ø–æ–∏—Å–∫–∞
    const clearSearch = async () => {
        setSearchQuery('');
        if (product.selectedCity) {
            await product.fetchProductsByCity(product.selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">
                    {product.selectedCity 
                        ? `–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ ${product.selectedCity}`
                        : '–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤'
                    }
                </h2>
                <div className="search-subtitle">
                    {product.selectedCity 
                        ? `–ò—â–∏—Ç–µ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é, –æ–ø–∏—Å–∞–Ω–∏—é –∏–ª–∏ —Ü–µ–Ω–µ –≤ –≤–∞—à–µ–º –≥–æ—Ä–æ–¥–µ`
                        : '–í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥ –¥–ª—è –ø–æ–∏—Å–∫–∞ —Ç–æ–≤–∞—Ä–æ–≤'
                    }
                </div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">üîç</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder={
                            product.selectedCity 
                                ? `–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ ${product.selectedCity}...`
                                : '–°–Ω–∞—á–∞–ª–∞ –≤—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥...'
                        }
                        className="search-input"
                        disabled={isSearching || !product.selectedCity}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ‚úï
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching || !product.selectedCity || !searchQuery.trim()}
                    >
                        {isSearching ? (
                            <div className="search-spinner"></div>
                        ) : (
                            '–ù–∞–π—Ç–∏'
                        )}
                    </button>
                </div>
            </form>

            {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ç–µ–∫—É—â–µ–º –ø–æ–∏—Å–∫–µ */}
            {product.hasActiveSearch && (
                <div className="search-results-info">
                    <div className="results-main">
                        <div className="results-count">
                            –ù–∞–π–¥–µ–Ω–æ —Ç–æ–≤–∞—Ä–æ–≤: <strong>{product.totalCount}</strong>
                        </div>
                        <div className="search-context">
                            –≤ –≥–æ—Ä–æ–¥–µ <strong>{product.selectedCity}</strong>
                        </div>
                    </div>
                    <div className="search-query">
                        –ü–æ –∑–∞–ø—Ä–æ—Å—É: "<strong>{product.currentSearchQuery}</strong>"
                    </div>
                    <button 
                        onClick={clearSearch}
                        className="clear-search-link"
                    >
                        –°–±—Ä–æ—Å–∏—Ç—å –ø–æ–∏—Å–∫
                    </button>
                </div>
            )}

            {/* –°–æ–æ–±—â–µ–Ω–∏–µ –µ—Å–ª–∏ –≥–æ—Ä–æ–¥ –Ω–µ –≤—ã–±—Ä–∞–Ω */}
            {!product.selectedCity && (
                <div className="no-city-message">
                    <div className="no-city-icon">üìç</div>
                    <div className="no-city-text">
                        <strong>–í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥</strong> –¥–ª—è –ø–æ–∏—Å–∫–∞ —Ç–æ–≤–∞—Ä–æ–≤
                    </div>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
