import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import { Row } from 'react-bootstrap';
import ProductItem from './ProductItem';
import '../css/components/ProductList.css';
const ProductList = observer(() => {
    const { product } = useContext(Context);

    // Добавляем проверки перед использованием map
    if (!product || !product.products || !Array.isArray(product.products)) {
        return (
            <Row className="d-flex">
                <div className="text-center w-100">
                    <p>Продукты загружаются или отсутствуют...</p>
                    {/* Можно добавить spinner или скелетон */}
                </div>
            </Row>
        );
    }

    // Проверяем, что массив не пустой
    if (product.products.length === 0) {
        return (
            <Row className="d-flex">
                <div className="text-center w-100">
                    <p>Нет доступных продуктов</p>
                </div>
            </Row>
        );
    }

    return (
        <Row className="d-flex">
            {product.products.map(productItem => (
                <ProductItem key={productItem.id} product={productItem} />
            ))}
        </Row>
    );
});

export default ProductList;
