// components/Pages.js
import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import { Pagination } from 'react-bootstrap';

const Pages = () => {
    const { product } = useContext(Context);
    const pageCount = Math.ceil(product.totalCount / product.limit);
    const pages = [];

    // Показываем только 5 страниц вокруг текущей
    let startPage = Math.max(1, product.page - 2);
    let endPage = Math.min(pageCount, product.page + 2);

    // Корректируем, если мы в начале или конце
    if (product.page <= 3) {
        endPage = Math.min(5, pageCount);
    }
    if (product.page >= pageCount - 2) {
        startPage = Math.max(1, pageCount - 4);
    }

    for (let i = startPage; i <= endPage; i++) {
        pages.push(i);
    }

    return (
        <div className="shop-pagination-container">
            <Pagination className="shop-pagination">
                {/* Кнопка "Назад" */}
                <Pagination.Prev 
                    disabled={product.page === 1}
                    onClick={() => {
                        if (product.page > 1) {
                            product.setPage(product.page - 1);
                            product.fetchProducts();
                        }
                    }}
                />

                {/* Первая страница */}
                {startPage > 1 && (
                    <>
                        <Pagination.Item
                            key={1}
                            active={product.page === 1}
                            onClick={() => {
                                product.setPage(1);
                                product.fetchProducts();
                            }}
                        >
                            1
                        </Pagination.Item>
                        {startPage > 2 && <Pagination.Ellipsis />}
                    </>
                )}

                {/* Основные страницы */}
                {pages.map(page => (
                    <Pagination.Item
                        key={page}
                        active={product.page === page}
                        onClick={() => {
                            product.setPage(page);
                            product.fetchProducts();
                        }}
                    >
                        {page}
                    </Pagination.Item>
                ))}

                {/* Последняя страница */}
                {endPage < pageCount && (
                    <>
                        {endPage < pageCount - 1 && <Pagination.Ellipsis />}
                        <Pagination.Item
                            key={pageCount}
                            active={product.page === pageCount}
                            onClick={() => {
                                product.setPage(pageCount);
                                product.fetchProducts();
                            }}
                        >
                            {pageCount}
                        </Pagination.Item>
                    </>
                )}

                {/* Кнопка "Вперед" */}
                <Pagination.Next 
                    disabled={product.page === pageCount}
                    onClick={() => {
                        if (product.page < pageCount) {
                            product.setPage(product.page + 1);
                            product.fetchProducts();
                        }
                    }}
                />
            </Pagination>
            
            {/* Информация о страницах */}
            <div className="pagination-info">
                Страница <strong>{product.page}</strong> из <strong>{pageCount}</strong> • 
                Показано <strong>{product.products.length}</strong> из <strong>{product.totalCount}</strong> товаров
            </div>
        </div>
    );
};

export default observer(Pages);
