// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        city: product.selectedCity || 'all',
        typeId: 'all'
    });

    // Сбрасываем фильтр города при изменении выбранного города
    useEffect(() => {
        if (product.selectedCity) {
            setFilters(prev => ({
                ...prev,
                city: product.selectedCity
            }));
        }
    }, [product.selectedCity]);

    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // Если поисковый запрос пустой, показываем обычные товары
            if (product.selectedCity) {
                await product.fetchProductsByCity(product.selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            const queryParams = new URLSearchParams({
                q: searchQuery.trim(),
                ...(filters.city && filters.city !== 'all' && { city: filters.city }),
                ...(filters.typeId && filters.typeId !== 'all' && { typeId: filters.typeId })
            });

            const response = await fetch(`http://localhost:5000/api/prod/search?${queryParams}`);
            
            if (response.ok) {
                const data = await response.json();
                product.setProducts(data.products);
                product.setTotalCount(data.totalCount);
                product.setPage(data.currentPage);
            } else {
                console.error('Ошибка при поиске');
            }
        } catch (error) {
            console.error('Ошибка при выполнении поиска:', error);
        } finally {
            setIsSearching(false);
        }
    };

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleFilterChange = (filterType, value) => {
        setFilters(prev => ({
            ...prev,
            [filterType]: value
        }));
    };

    const clearSearch = async () => {
        setSearchQuery('');
        if (product.selectedCity) {
            await product.fetchProductsByCity(product.selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    return (
        <div className="search-bar-container">
            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">🔍</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleInputChange}
                        placeholder="Поиск товаров по названию, описанию..."
                        className="search-input"
                        disabled={isSearching}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ✕
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching}
                    >
                        {isSearching ? '...' : 'Найти'}
                    </button>
                </div>
                
                <button
                    type="button"
                    onClick={() => setShowFilters(!showFilters)}
                    className="filters-toggle-button"
                >
                    {showFilters ? '▲' : '▼'} Фильтры
                </button>
            </form>

            {showFilters && (
                <div className="search-filters">
                    <div className="filter-group">
                        <label className="filter-label">Город:</label>
                        <select
                            value={filters.city}
                            onChange={(e) => handleFilterChange('city', e.target.value)}
                            className="filter-select"
                        >
                            <option value="all">Все города</option>
                            <option value={product.selectedCity}>
                                Только {product.selectedCity}
                            </option>
                            {product.cities.map(city => (
                                <option key={city} value={city}>
                                    {city}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="filter-group">
                        <label className="filter-label">Категория:</label>
                        <select
                            value={filters.typeId}
                            onChange={(e) => handleFilterChange('typeId', e.target.value)}
                            className="filter-select"
                        >
                            <option value="all">Все категории</option>
                            {/* Здесь можно добавить список типов, если он есть в store */}
                        </select>
                    </div>
                </div>
            )}

            {product.products.length > 0 && searchQuery && (
                <div className="search-results-info">
                    Найдено {product.totalCount} товаров по запросу: "{searchQuery}"
                    {filters.city !== 'all' && ` в городе ${filters.city}`}
                </div>
            )}
        </div>
    );
});

export default SearchBar;
