import React, { useContext } from "react";
import { Context } from "../index";
import { observer } from "mobx-react-lite";
import { Nav, Navbar, Container, Button } from 'react-bootstrap';

const NavBar = () => {
    const { user } = useContext(Context);

    return (
        <Navbar collapseOnSelect expand="lg" style={{ backgroundColor: '#ffff00' }}>
            <Container>
                <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="me-auto" />
                    {user.isAuth ? (
                        <Nav>
                            <Button variant="outline-dark">Админ панель</Button>
                            <Button variant="outline-dark" >Войти</Button>
                        </Nav>
                    ) : (
                        <Nav>
                            <Button variant="outline-dark" onClick={() => user.setIsAuth(true)}>
                                Авторизоваться
                            </Button>
                        </Nav>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default observer(NavBar);
