import React from 'react';
import { Card, Col } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useHistory } from 'react-router-dom';
import { PRODUCT_ROUTE } from '../utils/const'; // Убедитесь, что этот маршрут определен
import { observer } from 'mobx-react-lite';
const ProductItem = ({ product }) => {
    const history = useHistory();

    return (
        <Col md={3} onClick={() => history.push(PRODUCT_ROUTE + '/' + product.id)}>
            <Card style={{ width: 150, cursor: 'pointer' }} border={"light"}>
                <Image width={150} height={150} src={process.env.REACT_APP_API_URL + product.img} />
                <div className="text-black-50 mt-1 d-flex justify-content-between align-items-center">
                    <div>{product.name}</div>
                    <div>{product.price} $</div>
                </div>
                <div className="text-black-50">{product.city}</div>
            </Card>
        </Col>
    );
};

export default observer(ProductItem);
