// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [selectedCity, setSelectedCity] = useState(product.selectedCity || 'all');

    // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ–º –≤—ã–±—Ä–∞–Ω–Ω—ã–π –≥–æ—Ä–æ–¥ —Å store
    useEffect(() => {
        if (product.selectedCity) {
            setSelectedCity(product.selectedCity);
        }
    }, [product.selectedCity]);

    // –ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // –ï—Å–ª–∏ –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å –ø—É—Å—Ç–æ–π, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–≤–∞—Ä—ã –ø–æ –≤—ã–±—Ä–∞–Ω–Ω–æ–º—É –≥–æ—Ä–æ–¥—É
            if (selectedCity && selectedCity !== 'all') {
                await product.fetchProductsByCity(selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), { city: selectedCity });
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // –°–º–µ–Ω–∞ –≥–æ—Ä–æ–¥–∞
    const handleCityChange = async (city) => {
        setSelectedCity(city);
        
        if (city === 'all') {
            // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å–µ —Ç–æ–≤–∞—Ä—ã
            await product.fetchProducts();
        } else {
            // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–æ–≤–∞—Ä—ã –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ –≥–æ—Ä–æ–¥–∞
            await product.fetchProductsByCity(city);
        }
        
        // –ï—Å–ª–∏ –µ—Å—Ç—å –∞–∫—Ç–∏–≤–Ω—ã–π –ø–æ–∏—Å–∫, –æ–±–Ω–æ–≤–ª—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã
        if (searchQuery.trim()) {
            await product.searchProducts(searchQuery.trim(), { city });
        }
    };

    // –û—á–∏—Å—Ç–∫–∞ –ø–æ–∏—Å–∫–∞
    const clearSearch = async () => {
        setSearchQuery('');
        if (selectedCity && selectedCity !== 'all') {
            await product.fetchProductsByCity(selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    // –ë—ã—Å—Ç—Ä—ã–π –≤—ã–±–æ—Ä –ø–æ–ø—É–ª—è—Ä–Ω—ã—Ö –≥–æ—Ä–æ–¥–æ–≤
    const popularCities = ['–ú–∏–Ω—Å–∫', '–ë—Ä–µ—Å—Ç', '–ì—Ä–æ–¥–Ω–æ', '–ì–æ–º–µ–ª—å', '–í–∏—Ç–µ–±—Å–∫', '–ú–æ–≥–∏–ª—ë–≤'];

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">–ù–∞–π–¥–∏—Ç–µ —Ç–æ–≤–∞—Ä—ã –≤ –≤–∞—à–µ–º –≥–æ—Ä–æ–¥–µ</h2>
                <div className="search-subtitle">–ü–æ–∏—Å–∫ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é, –æ–ø–∏—Å–∞–Ω–∏—é –∏ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏—è –ø–æ –≥–æ—Ä–æ–¥—É</div>
            </div>

            {/* –ë—ã—Å—Ç—Ä—ã–π –≤—ã–±–æ—Ä –≥–æ—Ä–æ–¥–æ–≤ */}
            <div className="cities-quick-access">
                <div className="cities-label">–ü–æ–ø—É–ª—è—Ä–Ω—ã–µ –≥–æ—Ä–æ–¥–∞:</div>
                <div className="cities-buttons">
                    <button
                        className={`city-button ${selectedCity === 'all' ? 'active' : ''}`}
                        onClick={() => handleCityChange('all')}
                    >
                        –í—Å–µ –≥–æ—Ä–æ–¥–∞
                    </button>
                    {popularCities.map(city => (
                        <button
                            key={city}
                            className={`city-button ${selectedCity === city ? 'active' : ''}`}
                            onClick={() => handleCityChange(city)}
                        >
                            {city}
                        </button>
                    ))}
                </div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-main-section">
                    {/* –í—ã–±–æ—Ä –≥–æ—Ä–æ–¥–∞ */}
                    <div className="city-selector">
                        <label className="selector-label">–ì–æ—Ä–æ–¥</label>
                        <select
                            value={selectedCity}
                            onChange={(e) => handleCityChange(e.target.value)}
                            className="city-select"
                        >
                            <option value="all">–í—Å–µ –≥–æ—Ä–æ–¥–∞</option>
                            {product.cities && product.cities.map(city => (
                                <option key={city} value={city}>
                                    {city}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* –ü–æ–∏—Å–∫–æ–≤–∞—è —Å—Ç—Ä–æ–∫–∞ */}
                    <div className="search-input-section">
                        <label className="selector-label">–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤</label>
                        <div className="search-input-group">
                            <div className="search-icon">üîç</div>
                            <input
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSearch(e)}
                                placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞, –æ–ø–∏—Å–∞–Ω–∏–µ..."
                                className="search-input"
                                disabled={isSearching}
                            />
                            {searchQuery && (
                                <button
                                    type="button"
                                    onClick={clearSearch}
                                    className="clear-search-button"
                                    disabled={isSearching}
                                >
                                    ‚úï
                                </button>
                            )}
                        </div>
                    </div>

                    {/* –ö–Ω–æ–ø–∫–∞ –ø–æ–∏—Å–∫–∞ */}
                    <div className="search-button-section">
                        <label className="selector-label invisible">–ü–æ–∏—Å–∫</label>
                        <button 
                            type="submit" 
                            className="search-button"
                            disabled={isSearching}
                        >
                            {isSearching ? (
                                <div className="search-spinner"></div>
                            ) : (
                                '–ù–∞–π—Ç–∏'
                            )}
                        </button>
                    </div>
                </div>
            </form>

            {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ —Ç–µ–∫—É—â–µ–º –≤—ã–±–æ—Ä–µ */}
            {(selectedCity !== 'all' || product.hasActiveSearch) && (
                <div className="current-selection-info">
                    {selectedCity !== 'all' && (
                        <div className="selected-city">
                            <span className="info-label">–ì–æ—Ä–æ–¥:</span>
                            <strong>{selectedCity}</strong>
                        </div>
                    )}
                    {product.hasActiveSearch && (
                        <div className="search-query-info">
                            <span className="info-label">–ü–æ–∏—Å–∫:</span>
                            <strong>"{product.currentSearchQuery}"</strong>
                            <button 
                                onClick={clearSearch}
                                className="clear-search-small"
                            >
                                ‚úï
                            </button>
                        </div>
                    )}
                    {product.products.length > 0 && (
                        <div className="results-count">
                            –ù–∞–π–¥–µ–Ω–æ: <strong>{product.totalCount}</strong> —Ç–æ–≤–∞—Ä–æ–≤
                        </div>
                    )}
                </div>
            )}
        </div>
    );
});

export default SearchBar;
