// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [selectedCity, setSelectedCity] = useState(product.selectedCity || 'all');

    // Синхронизируем выбранный город с store
    useEffect(() => {
        if (product.selectedCity) {
            setSelectedCity(product.selectedCity);
        }
    }, [product.selectedCity]);

    // Поиск товаров
    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // Если поисковый запрос пустой, показываем товары по выбранному городу
            if (selectedCity && selectedCity !== 'all') {
                await product.fetchProductsByCity(selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), { city: selectedCity });
        } catch (error) {
            console.error('Ошибка при выполнении поиска:', error);
        } finally {
            setIsSearching(false);
        }
    };

    // Смена города
    const handleCityChange = async (city) => {
        setSelectedCity(city);
        
        if (city === 'all') {
            // Показываем все товары
            await product.fetchProducts();
        } else {
            // Показываем товары выбранного города
            await product.fetchProductsByCity(city);
        }
        
        // Если есть активный поиск, обновляем результаты
        if (searchQuery.trim()) {
            await product.searchProducts(searchQuery.trim(), { city });
        }
    };

    // Очистка поиска
    const clearSearch = async () => {
        setSearchQuery('');
        if (selectedCity && selectedCity !== 'all') {
            await product.fetchProductsByCity(selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    // Быстрый выбор популярных городов
    const popularCities = ['Минск', 'Брест', 'Гродно', 'Гомель', 'Витебск', 'Могилёв'];

    return (
        <div className="search-bar-container">
            <div className="search-header">
                <h2 className="search-title">Найдите товары в вашем городе</h2>
                <div className="search-subtitle">Поиск по названию, описанию и фильтрация по городу</div>
            </div>

            {/* Быстрый выбор городов */}
            <div className="cities-quick-access">
                <div className="cities-label">Популярные города:</div>
                <div className="cities-buttons">
                    <button
                        className={`city-button ${selectedCity === 'all' ? 'active' : ''}`}
                        onClick={() => handleCityChange('all')}
                    >
                        Все города
                    </button>
                    {popularCities.map(city => (
                        <button
                            key={city}
                            className={`city-button ${selectedCity === city ? 'active' : ''}`}
                            onClick={() => handleCityChange(city)}
                        >
                            {city}
                        </button>
                    ))}
                </div>
            </div>

            <form onSubmit={handleSearch} className="search-form">
                <div className="search-main-section">
                    {/* Выбор города */}
                    <div className="city-selector">
                        <label className="selector-label">Город</label>
                        <select
                            value={selectedCity}
                            onChange={(e) => handleCityChange(e.target.value)}
                            className="city-select"
                        >
                            <option value="all">Все города</option>
                            {product.cities && product.cities.map(city => (
                                <option key={city} value={city}>
                                    {city}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Поисковая строка */}
                    <div className="search-input-section">
                        <label className="selector-label">Поиск товаров</label>
                        <div className="search-input-group">
                            <div className="search-icon">🔍</div>
                            <input
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSearch(e)}
                                placeholder="Название товара, описание..."
                                className="search-input"
                                disabled={isSearching}
                            />
                            {searchQuery && (
                                <button
                                    type="button"
                                    onClick={clearSearch}
                                    className="clear-search-button"
                                    disabled={isSearching}
                                >
                                    ✕
                                </button>
                            )}
                        </div>
                    </div>

                    {/* Кнопка поиска */}
                    <div className="search-button-section">
                        <label className="selector-label invisible">Поиск</label>
                        <button 
                            type="submit" 
                            className="search-button"
                            disabled={isSearching}
                        >
                            {isSearching ? (
                                <div className="search-spinner"></div>
                            ) : (
                                'Найти'
                            )}
                        </button>
                    </div>
                </div>
            </form>

            {/* Информация о текущем выборе */}
            {(selectedCity !== 'all' || product.hasActiveSearch) && (
                <div className="current-selection-info">
                    {selectedCity !== 'all' && (
                        <div className="selected-city">
                            <span className="info-label">Город:</span>
                            <strong>{selectedCity}</strong>
                        </div>
                    )}
                    {product.hasActiveSearch && (
                        <div className="search-query-info">
                            <span className="info-label">Поиск:</span>
                            <strong>"{product.currentSearchQuery}"</strong>
                            <button 
                                onClick={clearSearch}
                                className="clear-search-small"
                            >
                                ✕
                            </button>
                        </div>
                    )}
                    {product.products.length > 0 && (
                        <div className="results-count">
                            Найдено: <strong>{product.totalCount}</strong> товаров
                        </div>
                    )}
                </div>
            )}
        </div>
    );
});

export default SearchBar;
