// pages/AddProductPage.js
import React, { useState, useContext } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert, Spinner } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import { Context } from '../index';
import { createProduct } from '../http/productAPI';
import { observer } from 'mobx-react-lite';
import '../css/pages/AddProductPage.css';

const AddProductPage = observer(() => {
    const { user } = useContext(Context);
    const history = useHistory();
    
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    
    const [formData, setFormData] = useState({
        name: '',
        price: '',
        description: '',
        city: '',
        typeId: '',
        img: null
    });
    
    const [characteristics, setCharacteristics] = useState([
        { title: '', description: '' }
    ]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleFileChange = (e) => {
        setFormData(prev => ({
            ...prev,
            img: e.target.files[0]
        }));
    };

    const handleCharacteristicChange = (index, field, value) => {
        const updatedCharacteristics = [...characteristics];
        updatedCharacteristics[index][field] = value;
        setCharacteristics(updatedCharacteristics);
    };

    const addCharacteristic = () => {
        setCharacteristics([...characteristics, { title: '', description: '' }]);
    };

    const removeCharacteristic = (index) => {
        if (characteristics.length > 1) {
            const updatedCharacteristics = [...characteristics];
            updatedCharacteristics.splice(index, 1);
            setCharacteristics(updatedCharacteristics);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!user.isAuth) {
            setError('Для добавления товара необходимо авторизоваться');
            return;
        }

        if (!formData.img) {
            setError('Пожалуйста, загрузите изображение товара');
            return;
        }

        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const productData = new FormData();
            productData.append('name', formData.name);
            productData.append('price', parseFloat(formData.price));
            productData.append('sellerId', user.user.id);
            productData.append('typeId', formData.typeId || 1);
            productData.append('city', formData.city);
            productData.append('description', formData.description);
            productData.append('img', formData.img);
            
            if (characteristics.some(char => char.title && char.description)) {
                productData.append('info', JSON.stringify(
                    characteristics.filter(char => char.title && char.description)
                ));
            }

            await createProduct(productData);
            
            setSuccess('Товар успешно добавлен!');
            setFormData({
                name: '',
                price: '',
                description: '',
                city: '',
                typeId: '',
                img: null
            });
            setCharacteristics([{ title: '', description: '' }]);
            
            // Очищаем input файла
            document.getElementById('productImage').value = '';
            
            setTimeout(() => {
                history.push('/');
            }, 2000);
            
        } catch (err) {
            console.error('Error adding product:', err);
            setError('Ошибка при добавлении товара. Попробуйте еще раз.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="add-product-container">
            <Container>
                <Row className="justify-content-center">
                    <Col md={10} lg={8}>
                        <div className="page-header">
                            <h1 className="page-title">Добавить новый товар</h1>
                            <p className="page-subtitle">Заполните информацию о вашем товаре</p>
                        </div>

                        {error && <Alert variant="danger">{error}</Alert>}
                        {success && <Alert variant="success">{success}</Alert>}

                        <Card className="product-form-card">
                            <Card.Body>
                                <Form onSubmit={handleSubmit}>
                                    <Row>
                                        <Col md={6}>
                                            {/* Основная информация */}
                                            <div className="form-section">
                                                <h3 className="section-title">Основная информация</h3>
                                                
                                                <Form.Group className="mb-3">
                                                    <Form.Label>Название товара *</Form.Label>
                                                    <Form.Control
                                                        type="text"
                                                        name="name"
                                                        value={formData.name}
                                                        onChange={handleInputChange}
                                                        placeholder="Введите название товара"
                                                        required
                                                    />
                                                </Form.Group>

                                                <Form.Group className="mb-3">
                                                    <Form.Label>Цена ($) *</Form.Label>
                                                    <Form.Control
                                                        type="number"
                                                        name="price"
                                                        value={formData.price}
                                                        onChange={handleInputChange}
                                                        placeholder="0.00"
                                                        step="0.01"
                                                        min="0"
                                                        required
                                                    />
                                                </Form.Group>

                                                <Form.Group className="mb-3">
                                                    <Form.Label>Город *</Form.Label>
                                                    <Form.Control
                                                        type="text"
                                                        name="city"
                                                        value={formData.city}
                                                        onChange={handleInputChange}
                                                        placeholder="Введите город"
                                                        required
                                                    />
                                                </Form.Group>

                                                <Form.Group className="mb-3">
                                                    <Form.Label>Описание</Form.Label>
                                                    <Form.Control
                                                        as="textarea"
                                                        rows={3}
                                                        name="description"
                                                        value={formData.description}
                                                        onChange={handleInputChange}
                                                        placeholder="Опишите ваш товар..."
                                                    />
                                                </Form.Group>
                                            </div>
                                        </Col>

                                        <Col md={6}>
                                            {/* Изображение и категория */}
                                            <div className="form-section">
                                                <h3 className="section-title">Изображение</h3>
                                                
                                                <Form.Group className="mb-4">
                                                    <Form.Label>Изображение товара *</Form.Label>
                                                    <Form.Control
                                                        id="productImage"
                                                        type="file"
                                                        accept="image/*"
                                                        onChange={handleFileChange}
                                                        required
                                                    />
                                                    <Form.Text className="text-muted">
                                                        Выберите изображение для вашего товара
                                                    </Form.Text>
                                                </Form.Group>

                                                {formData.img && (
                                                    <div className="image-preview">
                                                        <p>Предпросмотр:</p>
                                                        <img 
                                                            src={URL.createObjectURL(formData.img)} 
                                                            alt="Preview" 
                                                            className="preview-image"
                                                        />
                                                    </div>
                                                )}
                                            </div>
                                        </Col>
                                    </Row>

                                    {/* Характеристики */}
                                    <div className="form-section">
                                        <div className="section-header">
                                            <h3 className="section-title">Характеристики</h3>
                                            <Button 
                                                type="button" 
                                                variant="outline-secondary" 
                                                size="sm"
                                                onClick={addCharacteristic}
                                            >
                                                + Добавить характеристику
                                            </Button>
                                        </div>
                                        
                                        {characteristics.map((char, index) => (
                                            <Row key={index} className="characteristic-row">
                                                <Col md={5}>
                                                    <Form.Group className="mb-2">
                                                        <Form.Control
                                                            type="text"
                                                            placeholder="Название характеристики"
                                                            value={char.title}
                                                            onChange={(e) => handleCharacteristicChange(index, 'title', e.target.value)}
                                                        />
                                                    </Form.Group>
                                                </Col>
                                                <Col md={5}>
                                                    <Form.Group className="mb-2">
                                                        <Form.Control
                                                            type="text"
                                                            placeholder="Значение"
                                                            value={char.description}
                                                            onChange={(e) => handleCharacteristicChange(index, 'description', e.target.value)}
                                                        />
                                                    </Form.Group>
                                                </Col>
                                                <Col md={2}>
                                                    <Button
                                                        type="button"
                                                        variant="outline-danger"
                                                        size="sm"
                                                        onClick={() => removeCharacteristic(index)}
                                                        disabled={characteristics.length === 1}
                                                    >
                                                        ×
                                                    </Button>
                                                </Col>
                                            </Row>
                                        ))}
                                    </div>

                                    {/* Кнопки отправки */}
                                    <div className="form-actions">
                                        <Button 
                                            variant="outline-secondary" 
                                            onClick={() => history.goBack()}
                                            disabled={loading}
                                        >
                                            Отмена
                                        </Button>
                                        <Button 
                                            type="submit" 
                                            variant="primary" 
                                            disabled={loading}
                                            className="submit-button"
                                        >
                                            {loading ? (
                                                <>
                                                    <Spinner
                                                        as="span"
                                                        animation="border"
                                                        size="sm"
                                                        role="status"
                                                        aria-hidden="true"
                                                        className="me-2"
                                                    />
                                                    Добавляем...
                                                </>
                                            ) : (
                                                'Добавить товар'
                                            )}
                                        </Button>
                                    </div>
                                </Form>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    );
});

export default AddProductPage;
