import React, { useContext } from 'react';
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { Pagination } from "react-bootstrap";

const Pages = () => {
    const { product } = useContext(Context); // Измените на product
    const pageCount = Math.ceil(product.totalCount / product.limit); // Используйте product вместо device
    const pages = [];

    for (let i = 0; i < pageCount; i++) {
        pages.push(i + 1);
    }

    return (
        <Pagination className="mt-3">
            {pages.map(page => (
                <Pagination.Item
                    key={page}
                    active={product.page === page} // Используйте product вместо device
                    onClick={() => product.setPage(page)} // Используйте product вместо device
                >
                    {page}
                </Pagination.Item>
            ))}
        </Pagination>
    );
};

export default observer(Pages);
