import {$authHost, $host} from "./index";
export const fetchProductTypes = async () => {
    const { data } = await $host.get('api/product/types');
    return data;
};

export const fetchProductBrands = async () => {
    const { data } = await $host.get('api/product/brands');
    return data;
};

export const fetchProducts = async (typeId, brandId, page, limit) => {
    const { data } = await $host.get('api/product', {
        params: {
            typeId,
            brandId,
            page,
            limit,
        },
    });
    return data;
};
