// pages/Shop.js
import React, { useEffect, useContext } from 'react';
import ProductList from "../components/ProductList"; 
import Pages from '../components/Pages';
import { observer } from "mobx-react-lite";
import { Context } from '../index';
import '../css/pages/Shop.css'; // Убедитесь, что путь правильный

const Shop = observer(() => {
    const { product } = useContext(Context);

    useEffect(() => {
        const city = sessionStorage.getItem('city') || '';
        if (city) {
            product.fetchProductsByCity(city);
        } else {
            product.fetchProducts();
        }
    }, [product.page]);

    return (
        <div className="shop-container">
            <div className="shop-header">
                <h1 className="shop-title">Магазин</h1>
                <p className="shop-subtitle">Лучшие товары в вашем городе</p>
            </div>
            <div className="shop-content">
                <ProductList />
                {product.totalCount > product.limit && (
                    <div className="pagination-container">
                        <Pages />
                    </div>
                )}
            </div>
        </div>
    );
});

export default Shop;
