import React, { useContext, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductList from "../components/ProductList"; // Импортируйте компонент для отображения продуктов
import { observer } from "mobx-react-lite";
import { Context } from "../index";

import { productStore } from '../stores/ProductStore';


const Shop = () => {
    const { product } = useContext(Context);

    useEffect(() => {
        productStore.fetchProducts(); // Загрузка продуктов при монтировании компонента
    }, []);

     return (
        <Container>
            <Row>
              
                <Col md={9}>
                    <ProductList /> {/* Компонент для отображения списка продуктов */}
                    {/* <Pages /> Компонент для постраничной навигации */}
                </Col>
            </Row>
        </Container>
    );
};

export default observer(Shop);
