import React, { useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductList from "../components/ProductList"; 
import Pages from '../components/Pages'; // Импортируйте компонент Pages
import { observer } from "mobx-react-lite";
import { productStore } from '../store/ProductStore';

const Shop = () => {
    useEffect(() => {
        productStore.fetchProducts(); // Загрузка продуктов при монтировании компонента
    }, [productStore.page]); // Загрузка продуктов при изменении страницы

    return (
        <Container>
            <Row>
                <Col md={9}>
                    <ProductList />
                    <Pages /> {/* Добавьте компонент для постраничной навигации */}
                </Col>
            </Row>
        </Container>
    );
};

export default observer(Shop);
