// routes.js
import Admin from "./pages/Admin";
import Auth from "./pages/Auth";
import ProductPage from "./pages/ProductPage";
import Shop from "./pages/Shop";
import UserProfile from './pages/UserInfo';
import FavoritesPage from "./pages/FavoritesPage"; // Импортируем страницу избранного
import { ADMIN_ROUTE, SHOP_ROUTE, PROFILE_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, PRODUCT_ROUTE, FAVORITES_ROUTE } from "./utils/const";

export const authRoutes = [
    {
        path: ADMIN_ROUTE,
        Component: Admin
    },
    { 
        path: PROFILE_ROUTE, 
        Component: UserProfile 
    },
    {
        path: FAVORITES_ROUTE, // Добавляем маршрут для избранного
        Component: FavoritesPage
    }
];

export const publicRoutes = [
    {
        path: SHOP_ROUTE,
        Component: Shop
    },
    {
        path: LOGIN_ROUTE,
        Component: Auth
    },
    {
        path: REGISTRATION_ROUTE,
        Component: Auth
    },
    {
        path: PRODUCT_ROUTE + '/:id',
        Component: ProductPage
    }
];
