// components/SearchBar.js
import React, { useState, useContext, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [filters, setFilters] = useState({
        city: 'all',
        typeId: 'all'
    });

    // Инициализируем фильтры при загрузке
    useEffect(() => {
        if (product.selectedCity) {
            setFilters(prev => ({
                ...prev,
                city: product.selectedCity
            }));
        }
    }, [product.selectedCity]);

    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            // Если поисковый запрос пустой, показываем обычные товары
            if (product.selectedCity) {
                await product.fetchProductsByCity(product.selectedCity);
            } else {
                await product.fetchProducts();
            }
            return;
        }

        setIsSearching(true);
        
        try {
            await product.searchProducts(searchQuery.trim(), filters);
        } catch (error) {
            console.error('Ошибка при выполнении поиска:', error);
        } finally {
            setIsSearching(false);
        }
    };

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleFilterChange = (filterType, value) => {
        const newFilters = {
            ...filters,
            [filterType]: value
        };
        setFilters(newFilters);

        // Если есть активный поиск, обновляем результаты с новыми фильтрами
        if (product.hasActiveSearch && searchQuery.trim()) {
            product.searchProducts(searchQuery.trim(), newFilters);
        }
    };

    const clearSearch = async () => {
        setSearchQuery('');
        if (product.selectedCity) {
            await product.fetchProductsByCity(product.selectedCity);
        } else {
            await product.fetchProducts();
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    return (
        <div className="search-bar-container">
            <form onSubmit={handleSearch} className="search-form">
                <div className="search-input-group">
                    <div className="search-icon">🔍</div>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleInputChange}
                        onKeyPress={handleKeyPress}
                        placeholder="Поиск товаров по названию, описанию, цене..."
                        className="search-input"
                        disabled={isSearching}
                    />
                    {searchQuery && (
                        <button
                            type="button"
                            onClick={clearSearch}
                            className="clear-search-button"
                            disabled={isSearching}
                        >
                            ✕
                        </button>
                    )}
                    <button 
                        type="submit" 
                        className="search-button"
                        disabled={isSearching || !searchQuery.trim()}
                    >
                        {isSearching ? (
                            <div className="search-spinner"></div>
                        ) : (
                            'Найти'
                        )}
                    </button>
                </div>
                
                <div className="filters-section">
                    <button
                        type="button"
                        onClick={() => setShowFilters(!showFilters)}
                        className="filters-toggle-button"
                    >
                        {showFilters ? '▲ Скрыть фильтры' : '▼ Показать фильтры'}
                    </button>

                    {showFilters && (
                        <div className="search-filters">
                            <div className="filter-group">
                                <label className="filter-label">Город:</label>
                                <select
                                    value={filters.city}
                                    onChange={(e) => handleFilterChange('city', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="all">Все города</option>
                                    {product.cities && product.cities.map(city => (
                                        <option key={city} value={city}>
                                            {city}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <div className="filter-group">
                                <label className="filter-label">Категория:</label>
                                <select
                                    value={filters.typeId}
                                    onChange={(e) => handleFilterChange('typeId', e.target.value)}
                                    className="filter-select"
                                >
                                    <option value="all">Все категории</option>
                                    {/* Здесь можно добавить список типов */}
                                </select>
                            </div>
                        </div>
                    )}
                </div>
            </form>

            {product.hasActiveSearch && product.products.length > 0 && (
                <div className="search-results-info">
                    🔍 Найдено {product.totalCount} товаров по запросу: "{product.currentSearchQuery}"
                    {filters.city !== 'all' && ` в городе ${filters.city}`}
                    <button 
                        onClick={clearSearch}
                        className="clear-search-link"
                    >
                        Показать все товары
                    </button>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
