// components/SearchBar.js
import React, { useState, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import ShopSearchButton from './ShopSearchButton';
import '../css/components/SearchBar.css';

const SearchBar = observer(() => {
    const { product } = useContext(Context);
    const [searchQuery, setSearchQuery] = useState('');
    const [isSearching, setIsSearching] = useState(false);

    const handleSearch = async (e) => {
        e.preventDefault();
        
        if (!searchQuery.trim()) {
            await product.clearSearch();
            return;
        }

        setIsSearching(true);
        try {
            await product.searchProducts(searchQuery.trim());
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –ø–æ–∏—Å–∫–∞:', error);
        } finally {
            setIsSearching(false);
        }
    };

    const clearSearch = async () => {
        setSearchQuery('');
        await product.clearSearch();
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

    return (
        <div className="search-bar-container">
            <div className="search-bar-content">
                <form onSubmit={handleSearch} className="search-form">
                    <div className="search-input-group">
                        <div className="search-icon">üîç</div>
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            onKeyPress={handleKeyPress}
                            placeholder={
                                product.hasSelectedCity 
                                    ? `–ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ ${product.currentCity}...`
                                    : '–°–Ω–∞—á–∞–ª–∞ –≤—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥...'
                            }
                            className="search-input"
                            disabled={isSearching || !product.hasSelectedCity}
                        />
                        {searchQuery && (
                            <button
                                type="button"
                                onClick={clearSearch}
                                className="clear-search-button"
                                disabled={isSearching}
                            >
                                ‚úï
                            </button>
                        )}
                        <button 
                            type="submit" 
                            className="search-button"
                            disabled={isSearching || !product.hasSelectedCity || !searchQuery.trim()}
                        >
                            {isSearching ? (
                                <div className="search-spinner"></div>
                            ) : (
                                '–ù–∞–π—Ç–∏'
                            )}
                        </button>
                    </div>
                </form>

                <div className="shop-search-wrapper">
                    <ShopSearchButton />
                </div>
            </div>

            {!product.hasSelectedCity && (
                <div className="no-city-message">
                    <div className="no-city-text">
                        –í—ã–±–µ—Ä–∏—Ç–µ –≥–æ—Ä–æ–¥ –¥–ª—è –ø–æ–∏—Å–∫–∞ —Ç–æ–≤–∞—Ä–æ–≤
                    </div>
                </div>
            )}
        </div>
    );
});

export default SearchBar;
