// components/modals/CitySelector.js
import React, { useState, useEffect } from 'react';
import { Modal, Button, Tabs, Tab, Spinner, Form } from 'react-bootstrap';

const CitySelectorModal = ({ 
    show, 
    handleClose, 
    cities, 
    selectedCity, 
    handleCityChange,
    onDetectLocation,
    isLoading 
}) => {
    const [activeTab, setActiveTab] = useState('list');
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredCities, setFilteredCities] = useState([]);

    // Фильтрация городов при поиске
    useEffect(() => {
        if (searchTerm) {
            const filtered = cities.filter(city => 
                city.toLowerCase().includes(searchTerm.toLowerCase())
            );
            setFilteredCities(filtered);
        } else {
            setFilteredCities(cities);
        }
    }, [searchTerm, cities]);

    const handleTabSelect = (tab) => {
        setActiveTab(tab);
    };

    const handleCitySelectFromMap = (cityName) => {
        const mockEvent = {
            target: { value: cityName }
        };
        handleCityChange(mockEvent);
        handleClose();
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleCitySelectFromList = (city) => {
        const mockEvent = {
            target: { value: city }
        };
        handleCityChange(mockEvent);
        handleClose();
    };

    return (
        <Modal show={show} onHide={handleClose} size="lg">
            <Modal.Header closeButton>
                <Modal.Title>Выберите город</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Tabs activeKey={activeTab} onSelect={handleTabSelect} className="mb-3">
                    <Tab eventKey="list" title="Список городов">
                        <div style={{ marginBottom: '15px' }}>
                            <Button 
                                variant="outline-primary" 
                                onClick={onDetectLocation}
                                disabled={isLoading}
                                className="mb-3"
                                size="sm"
                            >
                                {isLoading ? (
                                    <>
                                        <Spinner animation="border" size="sm" className="me-2" />
                                        Определяем...
                                    </>
                                ) : (
                                    'Определить автоматически'
                                )}
                            </Button>

                            <Form.Group>
                                <Form.Label>Поиск города:</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Введите название города..."
                                    value={searchTerm}
                                    onChange={handleSearchChange}
                                />
                            </Form.Group>
                        </div>

                        <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
                            <Form.Label>Выберите город из списка:</Form.Label>
                            {filteredCities.length > 0 ? (
                                <div className="list-group">
                                    {filteredCities.map((city) => (
                                        <button
                                            key={city}
                                            type="button"
                                            className={`list-group-item list-group-item-action ${
                                                selectedCity === city ? 'active' : ''
                                            }`}
                                            onClick={() => handleCitySelectFromList(city)}
                                        >
                                            {city}
                                        </button>
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center text-muted py-3">
                                    {searchTerm ? 'Город не найден' : 'Загрузка городов...'}
                                </div>
                            )}
                        </div>
                    </Tab>

                    <Tab eventKey="map" title="На карте">
                        <div style={{ marginBottom: '15px' }}>
                            <p className="text-muted">
                                Выберите город кликом по названию на карте:
                            </p>
                            
                            <div 
                                style={{ 
                                    position: 'relative', 
                                    width: '100%', 
                                    height: '400px',
                                    backgroundImage: 'url(https://upload.wikimedia.org/wikipedia/commons/thumb/d/d3/Belarus_%28centered%29.svg/800px-Belarus_%28centered%29.svg.png)',
                                    backgroundSize: 'contain',
                                    backgroundRepeat: 'no-repeat',
                                    backgroundPosition: 'center',
                                    backgroundColor: '#f8f9fa',
                                    border: '1px solid #dee2e6',
                                    borderRadius: '4px'
                                }}
                            >
                                {/* Кликабельные кнопки для городов */}
                                <button
                                    style={{
                                        position: 'absolute',
                                        top: '42%',
                                        left: '45%',
                                        background: selectedCity === 'Минск' ? '#007bff' : 'rgba(255,255,255,0.9)',
                                        border: '1px solid #dee2e6',
                                        color: selectedCity === 'Минск' ? 'white' : '#007bff',
                                        fontWeight: 'bold',
                                        cursor: 'pointer',
                                        fontSize: '12px',
                                        padding: '4px 8px',
                                        borderRadius: '15px',
                                        boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                                    }}
                                    onClick={() => handleCitySelectFromMap('Минск')}
                                    title="Минск"
                                >
                                    Минск
                                </button>
                                
                                <button
                                    style={{
                                        position: 'absolute',
                                        top: '70%',
                                        left: '25%',
                                        background: selectedCity === 'Брест' ? '#007bff' : 'rgba(255,255,255,0.9)',
                                        border: '1px solid #dee2e6',
                                        color: selectedCity === 'Брест' ? 'white' : '#007bff',
                                        fontWeight: 'bold',
                                        cursor: 'pointer',
                                        fontSize: '12px',
                                        padding: '4px 8px',
                                        borderRadius: '15px',
                                        boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                                    }}
                                    onClick={() => handleCitySelectFromMap('Брест')}
                                    title="Брест"
                                >
                                    Брест
                                </button>
                                
                                {/* Добавьте другие города по аналогии */}
                            </div>
                        </div>
                    </Tab>
                </Tabs>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Закрыть
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CitySelectorModal;
